01648 bool Exception_Printf(bool iserror, XCHAR* title, XCHAR* mask, ...)
01649 {
01650   XSTRING outstring;
01651 
01652   va_list arg;
01653 
01654   va_start(arg, mask);
01655 
01656   outstring.FormatArg(mask, &arg);
01657 
01658   va_end(arg);
01659 
01660   bool do_log = false;
01661 
01662   #ifdef XLOG_ACTIVE
01663   if(GEN_XLOG.IsActive())
01664     {
01665       GEN_XLOG.SetFilters(__L(""));
01666 
01667       if(GEN_XLOG.AddEntry(iserror?XLOGLEVEL_ERROR:XLOGLEVEL_WARNING, title, false, outstring.Get()))
01668         {
01669           do_log = true;
01670         }
01671     }
01672   #endif
01673 
01674   if(allexceptiontext)
01675     {
01676       (*allexceptiontext) += outstring;
01677       (*allexceptiontext) += __L("\r\n");
01678     }
01679 
01680   #ifdef XTRACE_ACTIVE
01681   if(!do_log)
01682     {
01683       int level = iserror?4:0;
01684       XTRACE_PRINTCOLOR(level, outstring.Get());
01685     }
01686   #endif
01687 
01688   return true;
01689 }
