00351                   {
00352                     variant=row->Get(e);
00353                     if(variant.GetType()==(XVARIANT_TYPE)DB_SQL_VARIANT_TYPE_STRING)
00354                       {
00355                         DB_SQL_STRING* tablename = new DB_SQL_STRING();
00356                         if(tablename)
00357                           {
00358                             tablename->Set((XCHAR*)variant);
00359                             tables.Add(tablename);
00360                           }
00361                       }
00362                   }
00363                 result->Next();
00364               }
