00514 SNDITEM* SNDFACTORY::CreateItem(XCHAR* path)  
00515 {
00516   if(!soundactive)
00517     {
00518       return NULL;
00519     }
00520 
00521   if(!path)
00522     {
00523       return NULL;
00524     }
00525 
00526   SNDITEM* item = new SNDITEM();
00527   if(!item)
00528     {
00529       return NULL;
00530     }
00531 
00532   item->SetType(SNDITEM_TYPE_FILE);
00533 
00534   XPATH     xpath;
00535   XSTRING   ID;
00536 
00537   xpath = path;
00538 
00539   xpath.GetNamefileExt(ID);
00540 
00541   item->GetID()->Set(ID);
00542   if(item->GetID()->IsEmpty())
00543     {
00544       return NULL;
00545     }
00546 
00547   SNDFILE* soundfile = SNDFILE::Create(path);
00548   if(!soundfile)
00549     {
00550       delete item;
00551       return NULL;
00552     }
00553 
00554   item->SetStatus(SNDITEM_STATUS_NONE);
00555   item->SetSoundFile(soundfile);
00556 
00557   sounditems.Add(item);
00558   
00559   return item;
