00592 SNDITEM* SNDFACTORY::CreateItem(XDWORD frecuency, XDWORD duration)
00593 {
00594   if(!soundactive)
00595     {
00596       return NULL;
00597     }
00598 
00599   if((!frecuency) || (!duration))
00600     {
00601       return NULL;
