00605   if(!item)
00606     {
00607       return NULL;
00608     }
00609 
00610   item->SetType(SNDITEM_TYPE_NOTE);
00611 
00612   item->GetID()->Format(__L("frec: %d (Hz) - time: %d (msec)"), frecuency, duration);
00613 
00614   SNDNOTE* note = new SNDNOTE();
00615   if(!note)
00616     {
00617       delete item;
00618       return NULL;
