00657 bool SNDFACTORY::Sound_IsAnyActive()
00658 { 
00659   if(!soundactive)
00660     {
00661       return false;
00662     }
00663 
00664   XDWORD counter = 0;
00665 
00666   for(XDWORD c=0; c<sounditems.GetSize(); c++)
00667     {
00668       SNDITEM* item = sounditems.Get(c);
00669       if(item)
00670         {
00671           if((item->GetStatus() != SNDITEM_STATUS_END) && 
00672              (item->GetStatus() != SNDITEM_STATUS_NONE))
00673             {
00674               counter++;
00675             }
00676         }
00677     }
00678 
00679   return counter?true:false;                     
00680 }
