00259 bool SCRIPT_LNG_JAVASCRIPT::HaveMainFunction()
00260 {
00261   XSTRING mainfunctionname;
00262   XSTRING script;
00263   int     start = 0;
00264   int     end   = 0;
00265   XSTRING line;
00266 
00267   script = GetScript()->Get();
00268   if(script.IsEmpty()) return false;
00269 
00270   mainfunctionname = SCRIPT_LNG_JAVASCRIPT_MAINFUNCTIONNAME;
00271 
00272   int index = GetScript()->Find(mainfunctionname, false);
00273   if(index == XSTRING_NOTFOUND) return false;
00274 
00275   for(start=index; start>0; start--)
00276     {
00277       if((script.Get()[start] == __C('\r')) || (script.Get()[start] == __C('\n')))
00278         {
00279           break;
00280         }
00281     }
00282 
00283   for(end=index; end<(int)script.GetSize() ; end++)
00284     {
00285       if(script.Get()[end] == __C(')'))
00286         {
00287           break;
00288         }
00289     }
00290 
00291   script.Copy(start, end+1, line);
00292 
00293   line.DeleteCharacter(__C('\n'));
00294   line.DeleteCharacter(__C('\r'));
00295   line.DeleteCharacter(__C(' '));
00296 
00297   XSTRING searchmask;
00298 
00299   searchmask.Format(__L("function%s()"), mainfunctionname.Get());
00300 
00301   if(!line.Compare(searchmask)) return true;
00302 
00303   return false;
00304 }
