00154 void Call_Key_Press(SCRIPT_LIB* library, SCRIPT* script, XVECTOR<XVARIANT*>* params, XVARIANT* returnvalue)
00155 {
00156   if(!library)      return;
00157   if(!script)       return;
00158   if(!params)       return;
00159   if(!returnvalue)  return;
00160 
00161   returnvalue->Set();
00162 
00163   bool status  = false;
00164 
00165   if(!params->GetSize())
00166     {
00167       script->HaveError(SCRIPT_ERRORCODE_INSUF_PARAMS);
00168       return;
00169     }
00170 
00171   int key = 0;
00172   
00173   library->GetParamConverted(params->Get(0), key);
00174   
00175   INPSIMULATE* inpsimulate = GEN_INPFACTORY.CreateSimulator();
00176   if(!inpsimulate)
00177     {
00178       (*returnvalue) = status;
00179       return;
00180     }
00181   
00182   status = inpsimulate->Key_Press((XBYTE)key);
00183 
00184   GEN_INPFACTORY.DeleteSimulator(inpsimulate);
00185 
00186   (*returnvalue) = status;
00187 }
