00907 bool IsSimilarPixel(XDWORD origin, XDWORD target, XBYTE margin)
00908 {
00909   XBYTE* originRGBA = (XBYTE*)&origin;
00910   XBYTE* targetRGBA = (XBYTE*)&target;  
00911   bool   status     = false;
00912 
00913   int ncomponent = 0;
00914 
00915   for(int c=0; c<4; c++)
00916     {
00917       if(abs(originRGBA[c] - targetRGBA[c]) > margin)
00918         {
00919           ncomponent++;
00920         }
00921     }
00922 
00923   if(!ncomponent) 
00924     {
00925       status = true;
00926     }  
00927 
00928   return status;
00929 }
