00950   x = 0; 
00951   y = 0;
00952 
00953   if(!bitmapref)
00954     {
00955       return false;
00956     }
00957   
00958   GRPBITMAP* _bitmap = bitmapref->ConvertToMode(bitmapscreen->GetMode());
00959   if(!_bitmap)
00960     {
00961       return false;
00962     }
00963 
00964   if(_bitmap->GetMode() != bitmapscreen->GetMode()) 
00965     {
00966       return false;
00967     }
00968 
00969   if(!_bitmap->GetBuffer())
00970     {
00971       return false;
00972     }
00973   
00974   XDWORD*   bufferscreen        = (XDWORD*)bitmapscreen->GetBuffer();
00975   XDWORD*   bufferbitmap        = (XDWORD*)_bitmap->GetBuffer();
00976   XDWORD    sizepixel           = sizeof(XDWORD);
00977   XDWORD    bufferscreensize    = (bitmapscreen->GetBufferSize() / sizepixel) - _bitmap->GetWidth();
00978   XDWORD    bufferbmplinesize   = _bitmap->GetWidth();
00979   XDWORD    ndiff               = 0;
00980   bool      found               = false;
00981   
00982   for(XDWORD scrpos = 0; scrpos < bufferscreensize; scrpos++)
00983     {
00984       ndiff = 0;
00985       for(XDWORD bmppos = 0; bmppos < bufferbmplinesize; bmppos++)  
00986         {    
00987           if(bufferscreen[scrpos + bmppos] != bufferbitmap[bmppos])
00988             {
00989               if(!IsSimilarPixel(bufferscreen[scrpos + bmppos], bufferbitmap[bmppos], pixelmargin))
00990                 {
00991                   ndiff++;
00992                 }
00993                else
00994                 {
00995                   //FillLineDebug(bitmapscreen, bufferscreen, scrpos + bmppos, 1, 0xFF0000FF);
00996                 } 
00997             }
00998         }
00999                
01000       found = DifferencesPerCent(ndiff, bufferbmplinesize, difflimitpercent);
01001       if(found)
01002         {
01003           // FillLineDebug(bitmapscreen, bufferscreen, scrpos, bufferbmplinesize, 0xFF0000FF);
01004           
01005           found = false;
01006 
01007           XDWORD srcpixelsline  = bitmapscreen->GetWidth();
01008           XDWORD scrpos_tmp     = scrpos;
01009           XDWORD bmppos_tmp     = bufferbmplinesize;
01010          
01011           x =  (scrpos % bitmapscreen->GetWidth());
01012           y =  bitmapscreen->GetHeight() - (scrpos / srcpixelsline) - _bitmap->GetHeight();           
01013 
01014           scrpos_tmp += srcpixelsline;              
01015     
01016           for(XDWORD line = 1; line < _bitmap->GetHeight(); line++)
01017             {                                                   
01018               ndiff = 0;
01019               for(XDWORD bmppos = 0; bmppos < bufferbmplinesize; bmppos++)  
01020                 {    
01021                   if(scrpos_tmp + bmppos >= bufferscreensize)
01022                     {
01023                       ndiff += difflimitpercent;       
01024                       break;
01025                     }
01026 
01027                   if(bufferscreen[scrpos_tmp + bmppos] != bufferbitmap[bmppos_tmp])
01028                     {
01029                       if(!IsSimilarPixel(bufferscreen[scrpos_tmp + bmppos], bufferbitmap[bmppos_tmp], pixelmargin))
01030                         {
01031                           ndiff++;
01032                         }
01033                        else
01034                         {
01035                           // FillLineDebug(bitmapscreen, bufferscreen, scrpos_tmp + bmppos, 1, 0xFF0000FF);
01036                         } 
01037                     }  
01038                   
01039                   bmppos_tmp++;
01040                 }
01041               
01042               found = DifferencesPerCent(ndiff, bufferbmplinesize, difflimitpercent);              
01043 
01044               if(!found)
01045                 {
01046                   break;              
01047                 }
01048 
01049               //FillLineDebug(bitmapscreen, bufferscreen, scrpos_tmp, bufferbmplinesize, 0xFF0000FF);
01050 
01051               scrpos_tmp += srcpixelsline;
01052 
01053             }
01054 
01055           if(found)                  
01056             {
01057               break;
01058             }              
01059         }  
01060     } 
01061   
01062   if(!found)
01063     {
01064       x = 0;
01065       y = 0;
01066     }
01067    else
01068     {
01069       GRPVIEWPORT* viewport = NULL;
01070       GRPCANVAS*   canvas   = NULL;
01071       GRPSCREEN*   screen   = SCRIPT_LIB_WINDOW::GetAppGraphics()->GetMainScreen();
01072 
01073       if(screen) viewport = screen->GetViewport(0);
01074       if(viewport) canvas = viewport->GetCanvas();
01075 
01076       if(canvas)
01077         {
01078           GRP2DCOLOR_RGBA8  colorred(255, 0, 0);
01079 
01080           canvas->SetLineWidth(1.0f);
01081           canvas->SetLineColor(&colorred);
01082 
01083           canvas->Rectangle(x, y, x + _bitmap->GetWidth(), y + _bitmap->GetHeight());  
01084           canvas->PutPixel((x + _bitmap->GetWidth()/2), y + (_bitmap->GetHeight()/2), &colorred);
01085 
01086           screen->UpdateViewports();
01087         }      
01088     }
01089  
01090   delete _bitmap;
01091   
01092   return found;  
01093 }
