01109 bool PutBitmap(int x, int y, GRPBITMAP* bitmap)
01110 {
01111   if(!SCRIPT_LIB_WINDOW::GetAppGraphics())
01112     {
01113       return false;
01114     }
01115                                         
01116   GRPVIEWPORT* viewport = NULL;
01117   GRPCANVAS*   canvas   = NULL;
01118   GRPSCREEN*   screen   = SCRIPT_LIB_WINDOW::GetAppGraphics()->GetMainScreen();
01119 
01120   if(screen) viewport = screen->GetViewport(0);
01121   if(viewport) canvas = viewport->GetCanvas();
01122 
01123   if(canvas)
01124     {
01125       canvas->PutBitmap(x, y, bitmap);  
01126       screen->UpdateViewports();                                              
01127     }
01128   
01129   return true;
01130 }
