01147 GRPBITMAP* GetBitmap(int x, int y, int sizex, int sizey)
01148 {
01149   if(!SCRIPT_LIB_WINDOW::GetAppGraphics())
01150     {
01151       return NULL;
01152     }
01153                                         
01154   GRPVIEWPORT* viewport = NULL;
01155   GRPCANVAS*   canvas   = NULL;
01156   GRPSCREEN*   screen   = SCRIPT_LIB_WINDOW::GetAppGraphics()->GetMainScreen();
01157   GRPBITMAP*   bitmap   = NULL;
01158 
01159   if(screen) viewport = screen->GetViewport(0);
01160   if(viewport) canvas = viewport->GetCanvas();
01161 
01162   if(canvas)
01163     {
01164       bitmap = canvas->GetBitmap(x, y, sizex, sizey);  
01165       screen->UpdateViewports();                                              
01166     }
01167   
01168   return bitmap;
01169 }
