01185 void FillLineDebug(GRPBITMAP* bitmapscreen, XDWORD*  bufferscreen, XDWORD scrpos, XDWORD linesize, XDWORD color)
01186 {
01187   for(XDWORD c=scrpos; c<(scrpos + linesize); c++)
01188     {
01189       bufferscreen[c] = color; // 0xFF0000FF;
01190     }
01191 
01192   PutBitmap(0, 0, bitmapscreen);
01193 }
