00758 bool UI_ELEMENT::SwicthStateBlink()
00759 {
00760   if(!blink_nchanges)
00761     {
00762       blink_state = true;
00763       blink_nchanges++;
00764 
00765       return true;
00766     }
00767 
00768   blink_state = !blink_state;  
00769 
00770   blink_nchanges++;
00771 
00772   blink_xtimer->Reset();
00773 
00774   return true;
00775 }
