00510 UI_SKIN_TYPE UI_SKIN::GetType(XCHAR* name)
00511 {
00512   XSTRING _name;
00513 
00514   if(name) 
00515          _name = name;  
00516     else _name = this->name;
00517 
00518   if(_name.IsEmpty()) return UI_SKIN_TYPE_UNKNOWN;
00519 
00520   if(!_name.Compare(__L("flat"), true))       return UI_SKIN_TYPE_FLAT;
00521 
00522   return UI_SKIN_TYPE_UNKNOWN;
00523 }
