00748 bool UI_SKIN::CalculateBoundaryLine(UI_ELEMENT* element)
00749 {
00750   if(!element) return false;
00751 
00752   switch(element->GetType())
00753     {
00754       case UI_ELEMENT_TYPE_UNKNOWN              : return false;                                                     break;      
00755         case UI_ELEMENT_TYPE_SCROLL               : return CalculateBoundaryLine_Scroll(element);                     break;
00756         case UI_ELEMENT_TYPE_TEXT                     : return CalculateBoundaryLine_Text(element);                       break;    
00757       case UI_ELEMENT_TYPE_TEXTBOX            : return CalculateBoundaryLine_TextBox(element);                    break;    
00758         case UI_ELEMENT_TYPE_IMAGE              : return CalculateBoundaryLine_Image(element);                      break;
00759       case UI_ELEMENT_TYPE_ANIMATION            : return CalculateBoundaryLine_Animation(element);                  break;
00760       case UI_ELEMENT_TYPE_OPTION                   : return CalculateBoundaryLine_Option(element);                     break;
00761       case UI_ELEMENT_TYPE_MULTIOPTION      : return CalculateBoundaryLine_MultiOption(element);                break;
00762         case UI_ELEMENT_TYPE_BUTTON                 : return CalculateBoundaryLine_Button(element);                     break;              
00763       case UI_ELEMENT_TYPE_CHECKBOX             : return CalculateBoundaryLine_CheckBox(element);                   break;              
00764       case UI_ELEMENT_TYPE_EDITTEXT         : return CalculateBoundaryLine_EditText(element);                   break;        
00765       case UI_ELEMENT_TYPE_FORM                     : return CalculateBoundaryLine_Form(element);                       break; 
00766       case UI_ELEMENT_TYPE_MENU           : return CalculateBoundaryLine_Menu(element);                       break;                  
00767       case UI_ELEMENT_TYPE_LISTBOX        : return CalculateBoundaryLine_ListBox(element);                    break;                  
00768       case UI_ELEMENT_TYPE_PROGRESSBAR    : return CalculateBoundaryLine_ProgressBar(element);                break;                  
00769     }
00770 
00771   return false;
00772 }
