01025 bool UI_SKIN::CalculeBoundaryLine_AllElements(UI_ELEMENT* element, bool recursive)
01026 {
01027   CalculateBoundaryLine(element);
01028 
01029   for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
01030     {
01031       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
01032       if(subelement) CalculeBoundaryLine_AllElements(subelement, recursive);                   
01033     } 
01034 
01035   return true;
01036 }
