01100 bool UI_SKIN::Elements_SetToRedraw(UI_ELEMENT* element, bool recursive)
01101 {
01102   if(!element) return false;
01103 
01104   bool status = true;
01105 
01106   element->SetMustReDraw(true);
01107 
01108   if(recursive)
01109     {
01110       for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
01111         {
01112           UI_ELEMENT* subelement = (UI_ELEMENT*)element->GetComposeElements()->Get(c);
01113           if(subelement) 
01114             { 
01115               status = Elements_SetToRedraw(subelement, recursive);          
01116               
01117             } else break;
01118         }           
01119     }
01120 
01121   return status;
01122 }
