01136 bool UI_SKIN::Draw(UI_ELEMENT* element)  
01137 {
01138   if(!element) return false;
01139 
01140   if(!element->IsVisible())  return false;
01141 
01142   if(element->IsBlinking())
01143     {
01144       if(element->GetTimerBlink()->GetMeasureMilliSeconds() > element->IsBlinking()) 
01145         {
01146           element->SwicthStateBlink();                  
01147           Elements_SetToRedraw(element);
01148         }
01149        
01150       if(!element->GetStateBlink()) return false;        
01151     }
01152 
01153   bool status = false;
01154 
01155   switch(element->GetType())
01156     {
01157       case UI_ELEMENT_TYPE_UNKNOWN              : break;  
01158                              default      : break;
01159       case UI_ELEMENT_TYPE_SCROLL                 : status = Draw_Scroll(element);            break;
01160         case UI_ELEMENT_TYPE_TEXT                     : status = Draw_Text(element);              break; 
01161       case UI_ELEMENT_TYPE_TEXTBOX              : status = Draw_TextBox(element);           break;      
01162         case UI_ELEMENT_TYPE_IMAGE              : status = Draw_Image(element);             break;
01163       case UI_ELEMENT_TYPE_ANIMATION            : status = Draw_Animation(element);         break;
01164       case UI_ELEMENT_TYPE_OPTION                   : status = Draw_Option(element);            break;
01165       case UI_ELEMENT_TYPE_MULTIOPTION      : status = Draw_MultiOption(element);       break;
01166         case UI_ELEMENT_TYPE_BUTTON                 : status = Draw_Button(element);            break;              
01167       case UI_ELEMENT_TYPE_CHECKBOX             : status = Draw_CheckBox(element);          break;              
01168       case UI_ELEMENT_TYPE_EDITTEXT       : status = Draw_EditText(element);          break;          
01169       case UI_ELEMENT_TYPE_FORM                     : status = Draw_Form(element);              break;
01170       case UI_ELEMENT_TYPE_MENU           : status = Draw_Menu(element);              break;             
01171       case UI_ELEMENT_TYPE_LISTBOX        : status = Draw_ListBox(element);           break;             
01172       case UI_ELEMENT_TYPE_PROGRESSBAR    : status = Draw_ProgressBar(element);       break;                       
01173     }
01174 
01175   return status;
01176 } 
