00140 bool UI_SKINCANVAS_REBUILDAREAS::RebuildAllAreas()
00141 {   
00142   XDWORD max_z_level = 0;
00143   XDWORD nareas      = areas.GetSize();
00144   bool   first       =  false;
00145 
00146   if(!nareas) return true;
00147 
00148   for(XDWORD c=0; c<nareas; c++)
00149     {
00150       GRP2DREBUILDAREA* area = areas.Get(c);
00151       if(area)
00152         {
00153           UI_ELEMENT* element = (UI_ELEMENT*)area->GetExtraData();
00154           if(element)
00155             {
00156               max_z_level =  __MAX(max_z_level, element->GetZLevel()); 
00157             }
00158         }
00159     }
00160 
00161   if(!max_z_level) return false;
00162 
00163   for(XDWORD level = max_z_level; level>0; level--)  
00164     {
00165       nareas = areas.GetSize();  
00166 
00167       for(int index = nareas-1; index>=0; index--)  
00168         {
00169           GRP2DREBUILDAREA* area = areas.Get(index);
00170           if(area)
00171             {
00172               UI_ELEMENT* element = (UI_ELEMENT*)area->GetExtraData();
00173               if(element)
00174                 {
00175                   if(element->MustReDraw() || (!element->IsVisible())) 
00176                     { 
00177                       if(element->GetZLevel() == level)
00178                         {       
00179                           // XTRACE_PRINTCOLOR(XTRACE_COLOR_PURPLE, __L("Del area level [%d] [%s] "), element->GetZLevel(), element->GetName()->Get());
00180                           GRPBITMAP* bitmap = area->GetBitmap();              
00181                           if(bitmap) PutBitmapNoAlpha(area->GetXPos(), area->GetYPos(), bitmap);
00182 
00183                           areas.Delete(area);                
00184                           delete area;  
00185                         }              
00186                     }
00187                 }
00188             }           
00189         }
00190     }  
00191 
00192   return true;
00193 }
