00492 bool UI_SKINCANVAS::LoadFonts()
00493 {
00494   XPATH  xpath;
00495   bool   status = false; 
00496 
00497   GRPCANVAS* canvas = GetCanvas();
00498   if(!canvas) return false;
00499   
00500   if(!rasterfontname.IsEmpty())
00501     {
00502       status = canvas->RasterFont_Select(rasterfontname.Get());
00503 
00504     } else status = true;
00505 
00506   if(status)
00507     {
00508       if(!vectorfontname.IsEmpty())
00509         {          
00510           GEN_XPATHSMANAGER.GetPathOfSection(XPATHSMANAGERSECTIONTYPE_FONTS, xpath);
00511           xpath.Slash_Add();
00512           xpath.Add(vectorfontname.Get());
00513 
00514           if(GEN_USERINTERFACE.IsZippedFile())
00515             {             
00516               XFILEUNZIP* unzipfile = GEN_USERINTERFACE.GetUnzipFile();
00517               if(unzipfile)
00518                 {                  
00519                   XPATH pathnamefilecmp;
00520 
00521                   pathnamefilecmp = APPDEFAULT_DIRECTORY_FONTS;
00522                   pathnamefilecmp.Slash_Add();
00523                   pathnamefilecmp += vectorfontname;
00524 
00525                   status = unzipfile->DecompressFile(pathnamefilecmp, (*GEN_USERINTERFACE.GetUnzipPathFile()), vectorfontname.Get());   
00526                   if(status)
00527                     {  
00528                       XPATH unzippathfile_tmp;
00529 
00530                       unzippathfile_tmp  = GEN_USERINTERFACE.GetUnzipPathFile()->Get();
00531                       unzippathfile_tmp += vectorfontname;
00532 
00533                       status = canvas->VectorFont_Load(unzippathfile_tmp);  
00534 
00535                       //GEN_USERINTERFACE.DeleteTemporalUnZipFile(unzippathfile_tmp);  
00536 
00537                       fontpathfile = unzippathfile_tmp;
00538                     }
00539                     
00540                 }
00541             }
00542            else
00543             {                            
00544               status = canvas->VectorFont_Load(xpath);    
00545             }
00546 
00547         } else status = true;
00548     }
00549 
00550   return status;
00551 }
