00563 bool UI_SKINCANVAS::Background_LoadBitmap()
00564 {
00565   if(!screen) return false;
00566 
00567   XPATH           xpath;
00568   GRPBITMAPFILE*  bitmapfile;
00569   bool            status = false;
00570 
00571   bitmapfile = new GRPBITMAPFILE();
00572   if(!bitmapfile) 
00573     {
00574       return status;
00575     }
00576 
00577   GEN_XPATHSMANAGER.GetPathOfSection(XPATHSMANAGERSECTIONTYPE_GRAPHICS, xpath);
00578   xpath.Slash_Add();
00579   xpath.Add(background_namefile.Get());
00580 
00581   if(GEN_USERINTERFACE.IsZippedFile())
00582     {
00583       XFILEUNZIP* unzipfile = GEN_USERINTERFACE.GetUnzipFile();
00584       if(unzipfile)
00585         {                
00586           XPATH pathnamefilecmp;
00587 
00588           pathnamefilecmp = APPDEFAULT_DIRECTORY_GRAPHICS;
00589           pathnamefilecmp.Slash_Add();
00590           pathnamefilecmp += background_namefile;
00591  
00592           status = unzipfile->DecompressFile(pathnamefilecmp, (*GEN_USERINTERFACE.GetUnzipPathFile()), background_namefile.Get());   
00593           if(status)
00594             {  
00595               XPATH unzippathfile_tmp;
00596 
00597               unzippathfile_tmp  = GEN_USERINTERFACE.GetUnzipPathFile()->Get();
00598               unzippathfile_tmp += background_namefile;
00599 
00600               background_bitmap = bitmapfile->Load(unzippathfile_tmp, screen->GetMode());
00601               if(background_bitmap) 
00602                 {            
00603                   status = true;
00604                 }
00605 
00606               GEN_USERINTERFACE.DeleteTemporalUnZipFile(unzippathfile_tmp);  
00607             }
00608         }
00609     }
00610    else
00611     {                                          
00612       background_bitmap = bitmapfile->Load(xpath, screen->GetMode());
00613       if(background_bitmap) 
00614         {            
00615           status = true;
00616         }
00617     }
00618 
00619   delete bitmapfile;
00620 
00621   return status;
00622 
00623 }
