00639 bool UI_SKINCANVAS::GetFatherSize(UI_ELEMENT* element, double& width, double& height)
00640 {
00641   GRPCANVAS* canvas = GetCanvas();
00642   if(!canvas) return false;
00643 
00644   width  = 0.0f;
00645   height = 0.0f;
00646   
00647   if(element->GetFather())  
00648     {
00649       width  = element->GetFather()->GetBoundaryLine()->width; 
00650       height = element->GetFather()->GetBoundaryLine()->height; 
00651     }
00652    else  
00653     { 
00654       width  = canvas->GetWidth()-1;   
00655       height = canvas->GetHeight()-1;   
00656     }
00657 
00658   return true;
00659 }
