00673 double UI_SKINCANVAS::GetPositionWithoutDefine(double position)
00674 { 
00675   switch((int)position)
00676     {
00677       case UI_ELEMENT_TYPE_ALIGN_LEFT     : break;
00678       case UI_ELEMENT_TYPE_ALIGN_RIGHT      : break;
00679       case UI_ELEMENT_TYPE_ALIGN_UP       : break;
00680       case UI_ELEMENT_TYPE_ALIGN_DOWN       : break;
00681       case UI_ELEMENT_TYPE_ALIGN_CENTER     : break;
00682                         default       : return position;
00683     } 
00684     
00685   return 0.0f;
00686 }
