00700 bool UI_SKINCANVAS::SetAroundFromSubElements(UI_ELEMENT* element)
00701 {
00702   if(!element) return false;
00703   if(element->GetComposeElements()->IsEmpty()) return false;
00704 
00705   UI_BOUNDARYLINE bondaryline[2];
00706 
00707   bondaryline[1].x      = element->GetXPosition();
00708   bondaryline[1].y      = element->GetYPosition();
00709   bondaryline[1].width  = element->GetBoundaryLine()->width;
00710   bondaryline[1].height = element->GetBoundaryLine()->height;
00711 
00712   for(XDWORD c=0; c<element->GetComposeElements()->GetSize(); c++)
00713     {
00714       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
00715       if(subelement)
00716         {
00717           bondaryline[0].x       = subelement->GetXPosition();
00718           bondaryline[0].y       = subelement->GetYPosition();
00719           bondaryline[0].width   = subelement->GetBoundaryLine()->width;
00720           bondaryline[0].height  = subelement->GetBoundaryLine()->height;
00721 
00722           bondaryline[1].SetAround(bondaryline[0]);
00723         }
00724     }
00725 
00726 
00727   for(int c=(int)element->GetComposeElements()->GetSize()-1; c>=0; c--)
00728     {
00729       UI_ELEMENT* subelement = element->GetComposeElements()->Get(c);
00730       if(subelement)
00731         {
00732           bondaryline[0].x       = subelement->GetXPosition();
00733           bondaryline[0].y       = subelement->GetYPosition();
00734           bondaryline[0].width   = subelement->GetBoundaryLine()->width;
00735           bondaryline[0].height  = subelement->GetBoundaryLine()->height;
00736 
00737           bondaryline[1].SetAround(bondaryline[0]);
00738         }
00739     }
00740  
00741   element->SetXPosition(bondaryline[1].x);
00742   element->SetYPosition(bondaryline[1].y);
00743   element->GetBoundaryLine()->width  = bondaryline[1].width;
00744   element->GetBoundaryLine()->height = bondaryline[1].height;
00745 
00746   return true;
00747 }
