00792 bool UI_SKINCANVAS::CalculePosition(UI_ELEMENT* element, double fatherwidth, double fatherheight)
00793 { 
00794   if(!element) return false;
00795 
00796   double x_position = 0.0f;
00797   double y_position = 0.0f;
00798 
00799   if(element->GetFather())
00800     {
00801       x_position = GetPositionWithoutDefine(element->GetFather()->GetXPosition());
00802       y_position = GetPositionWithoutDefine(element->GetFather()->GetYPosition());     
00803     }
00804        
00805   switch((int)element->GetBoundaryLine()->x)
00806     {
00807       case UI_ELEMENT_TYPE_ALIGN_LEFT     : x_position += 0;                                                                  break;
00808       case UI_ELEMENT_TYPE_ALIGN_RIGHT      : x_position += (fatherwidth - element->GetBoundaryLine()->width);                  break;
00809       case UI_ELEMENT_TYPE_ALIGN_CENTER   : x_position += (int)round((fatherwidth - element->GetBoundaryLine()->width)/2);    break;
00810                                 default   : x_position += GetPositionWithoutDefine(element->GetBoundaryLine()->x);            break;
00811     }
00812 
00813   if(element->GetFather())
00814     {
00815       switch((int)element->GetBoundaryLine()->y)
00816         {
00817           case UI_ELEMENT_TYPE_ALIGN_UP       : y_position -= fatherheight -element->GetBoundaryLine()->height;                   break;
00818           case UI_ELEMENT_TYPE_ALIGN_DOWN       : y_position -= 0;                                                                  break;
00819           case UI_ELEMENT_TYPE_ALIGN_CENTER     : y_position -= (int)round((fatherheight - element->GetBoundaryLine()->height)/2);  break;
00820                                     default   : y_position -= GetPositionWithoutDefine(element->GetBoundaryLine()->y);            break;
00821         }
00822      
00823     }
00824    else 
00825     {
00826       switch((int)element->GetBoundaryLine()->y)
00827         {
00828           case UI_ELEMENT_TYPE_ALIGN_UP       : y_position += element->GetBoundaryLine()->height;                                 break;
00829           case UI_ELEMENT_TYPE_ALIGN_DOWN       : y_position += fatherheight;                                                       break;
00830           case UI_ELEMENT_TYPE_ALIGN_CENTER     : y_position += (int)round((fatherheight + element->GetBoundaryLine()->height)/2);  break;
00831                                     default   : y_position += GetPositionWithoutDefine(element->GetBoundaryLine()->y);            break;
00832         }
00833     }
00834 
00835   x_position += (element->GetMargin(UI_ELEMENT_TYPE_ALIGN_LEFT) - element->GetMargin(UI_ELEMENT_TYPE_ALIGN_RIGHT));
00836   y_position += (element->GetMargin(UI_ELEMENT_TYPE_ALIGN_UP)   - element->GetMargin(UI_ELEMENT_TYPE_ALIGN_DOWN));
00837 
00838   element->SetXPosition(x_position);
00839   element->SetYPosition(y_position);
00840 
00841   return true;
00842 }
