00909 
00921 bool UI_SKINCANVAS::CalculateBoundaryLine_Text(UI_ELEMENT* element)
00922 {
00923   if(!element) return false;
00924 
00925   UI_ELEMENT_TEXT* element_text = (UI_ELEMENT_TEXT*)element;
00926   GRPCANVAS*       canvas       = GetCanvas();
00927 
00928   if(!canvas) return false;
00929   
00930   canvas->Vectorfont_GetConfig()->SetSize(element_text->GetSizeFont());
00931 
00932   XSTRING text;
00933   if(!element_text->GetText()->IsEmpty()) 
00934     {
00935       text = element_text->GetText()->Get();    
00936     }
00937    else 
00938     {
00939       if(!element_text->GetMaskText()->IsEmpty()) 
00940         {
00941           text = element_text->GetMaskText()->Get();
00942         }
00943     }
00944 
00945   double fatherwidth  = 0.0f;
00946   double fatherheight = 0.0f;
00947   
00948   GetFatherSize(element, fatherwidth, fatherheight);
00949 
00950   XSTRING string_max = text;
00951   if(element_text->GetMaxSizeText())
00952     {
00953       XDWORD size_max = element_text->GetMaxSizeText();     
00954       
00955       if(!size_max) size_max = text.GetSize();
00956       string_max.Empty();
00957       for(XDWORD c=0; c<size_max; c++)
00958         {
00959           string_max += __L("A");
00960         }
00961     }
00962 
00963   switch((int)element->GetBoundaryLine()->width)
00964     {
00965       case UI_ELEMENT_TYPE_ALIGN_AUTO    :              
00966       case                           0 : element->GetBoundaryLine()->width  = (double)canvas->VectorFont_GetWidth(string_max.Get());    break;
00967       case UI_ELEMENT_TYPE_ALIGN_MAX   : element->GetBoundaryLine()->width = fatherwidth;                                               break;      
00968     }
00969    
00970   switch((int)element->GetBoundaryLine()->height)
00971     {
00972       case UI_ELEMENT_TYPE_ALIGN_AUTO    :              
00973       case                           0 : element->GetBoundaryLine()->height  = (double)canvas->VectorFont_GetHeight(string_max.Get());  break;
00974       case UI_ELEMENT_TYPE_ALIGN_MAX   : element->GetBoundaryLine()->height  = fatherheight;                                            break;      
00975     }
00976 
00977   return CalculePosition(element, fatherwidth, fatherheight);
00978 }
