01033 bool UI_SKINCANVAS::CalculateBoundaryLine_Image(UI_ELEMENT* element)
01034 {
01035   if(!element) return false;
01036   
01037   UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element;
01038   if(!element_image->GetImage()) return false;
01039 
01040   double fatherwidth  = 0.0f;
01041   double fatherheight = 0.0f;
01042   
01043   GetFatherSize(element, fatherwidth, fatherheight);
01044 
01045   switch((int)element->GetBoundaryLine()->width)
01046     {
01047       case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01048       case                            0 : element->GetBoundaryLine()->width  = (double)element_image->GetImage()->GetWidth();       break;
01049       case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->width = fatherwidth;                                          break;      
01050     }
01051    
01052   switch((int)element->GetBoundaryLine()->height)
01053     {
01054       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01055       case                            0 : element->GetBoundaryLine()->height  = (double)element_image->GetImage()->GetHeight();     break;
01056       case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->height  = fatherheight;                                       break;      
01057     }
01058 
01059   return CalculePosition(element, fatherwidth, fatherheight);
01060 }
