01074 bool UI_SKINCANVAS::CalculateBoundaryLine_Animation(UI_ELEMENT* element)
01075 {
01076   UI_ELEMENT_ANIMATION* element_animation = (UI_ELEMENT_ANIMATION*)element;
01077   if(!element_animation) return false;  
01078 
01079   double fatherwidth  = 0.0f;
01080   double fatherheight = 0.0f;
01081   
01082   GetFatherSize(element, fatherwidth, fatherheight);
01083 
01084   switch((int)element->GetBoundaryLine()->width)
01085     {
01086       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01087       case                             0  : { double maximgwidth = 0.0f; 
01088 
01089                                               for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01090                                                 {
01091                                                  UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01092                                                  if(element_image) maximgwidth = __MAX((GetPositionWithoutDefine(element_image->GetBoundaryLine()->x) + element_image->GetBoundaryLine()->width), maximgwidth);                                                    
01093                                                 }
01094 
01095                                               element->GetBoundaryLine()->width = maximgwidth;
01096                                             }
01097                                             break;
01098 
01099       case UI_ELEMENT_TYPE_ALIGN_MAX      : element->GetBoundaryLine()->width = fatherwidth;                                          
01100                                             break;      
01101     }
01102    
01103   switch((int)element->GetBoundaryLine()->height)
01104     {
01105       case UI_ELEMENT_TYPE_ALIGN_AUTO     :             
01106       case                             0  : { double maximgheight = 0.0f; 
01107 
01108                                               for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01109                                                 {
01110                                                  UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01111                                                  if(element_image) maximgheight = __MAX((GetPositionWithoutDefine(element_image->GetBoundaryLine()->y) + element_image->GetBoundaryLine()->height), maximgheight);                                                    
01112                                                 }
01113 
01114                                               element->GetBoundaryLine()->height = maximgheight;
01115                                             }
01116                                             break;
01117 
01118       case UI_ELEMENT_TYPE_ALIGN_MAX      : element->GetBoundaryLine()->height  = fatherheight;                                       
01119                                             break;      
01120     }
01121 
01122   CalculePosition(element, fatherwidth, fatherheight);
01123 
01124   for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01125     {
01126       UI_ELEMENT_IMAGE* element_image = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(c);
01127       if(element_image)
01128         {
01129           CalculePosition(element_image, element_animation->GetBoundaryLine()->width, element_image->GetBoundaryLine()->height);
01130         }
01131     }
01132 
01133   return true;
01134 }
