01136 
01148 bool UI_SKINCANVAS::CalculateBoundaryLine_Option(UI_ELEMENT* element)
01149 {
01150   UI_ELEMENT_OPTION* element_option = (UI_ELEMENT_OPTION*)element;
01151   if(!element_option) return false;  
01152 
01153   double fatherwidth  = 0.0f;
01154   double fatherheight = 0.0f;
01155   
01156   GetFatherSize(element, fatherwidth, fatherheight);
01157 
01158   UI_ELEMENT_ANIMATION* element_animation = (UI_ELEMENT_ANIMATION*)element_option->Get_UIAnimation();
01159   UI_ELEMENT_TEXT*      element_text      = (UI_ELEMENT_TEXT*)element_option->Get_UIText();
01160 
01161   double maximgwidth  = 0.0f; 
01162   double maximgheight = 0.0f; 
01163 
01164   if(element_animation || element_text)
01165     {
01166       switch((int)element->GetBoundaryLine()->width)
01167         {
01168           case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01169           case                           0  : { switch(element_option->GetAllocationTextType())
01170                                                   {                                            
01171                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : 
01172                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation) maximgwidth += element_animation->GetBoundaryLine()->width;  
01173                                                                                                           if(element_text)      maximgwidth += element_text->GetBoundaryLine()->width;  
01174                                                                                                           break;
01175 
01176                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    :                                                                  
01177                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : 
01178                                                                                               default   : if(element_animation) maximgwidth = __MAX(maximgwidth, element_animation->GetBoundaryLine()->width);                                                                                                    
01179                                                                                                           if(element_text)      maximgwidth = __MAX(maximgwidth, element_text->GetBoundaryLine()->width);                                                                                                  
01180                                                                                                           break; 
01181                                                   } 
01182                                      
01183                                                 element->GetBoundaryLine()->width = maximgwidth;
01184                                               }
01185                                               break;
01186 
01187           case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->width = fatherwidth;                                          
01188                                               break;        
01189         }
01190 
01191       switch((int)element->GetBoundaryLine()->height)
01192         {
01193           case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01194           case                           0  : { switch(element_option->GetAllocationTextType())
01195                                                   {                                           
01196                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : 
01197                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation) maximgheight += element_animation->GetBoundaryLine()->height;  
01198                                                                                                           if(element_text)      maximgheight += element_text->GetBoundaryLine()->height;  
01199                                                                                                           break;
01200 
01201                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : 
01202                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : 
01203                                                                                               default   : if(element_animation) maximgheight = __MAX(maximgheight, element_animation->GetBoundaryLine()->height);                                                                                                    
01204                                                                                                           if(element_text)      maximgheight = __MAX(maximgheight, element_text->GetBoundaryLine()->height);                                                                                                  
01205                                                                                                           break; 
01206                                                   }                                      
01207                                                 element->GetBoundaryLine()->height = maximgheight;
01208                                               }
01209                                               break;
01210 
01211           case UI_ELEMENT_TYPE_ALIGN_MAX    : element->GetBoundaryLine()->height  = fatherheight;                                       
01212                                               break;        
01213         }
01214     }
01215   
01216   CalculePosition(element_option, fatherwidth, fatherheight);
01217 
01218   if(element_animation) CalculePosition(element_animation, element_option->GetBoundaryLine()->width, element_option->GetBoundaryLine()->height);
01219   if(element_text)      CalculePosition(element_text     , element_option->GetBoundaryLine()->width, element_option->GetBoundaryLine()->height);  
01220 
01221   switch(element_option->GetAllocationTextType())
01222     {
01223       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : break;
01224 
01225       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation && element_text) element_animation->SetYPosition(element_animation->GetYPosition() - element_text->GetBoundaryLine()->height);     break;
01226       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : if(element_animation && element_text) element_text->SetYPosition(element_text->GetYPosition() - element_animation->GetBoundaryLine()->height);          break;                                                           
01227       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : if(element_animation && element_text) element_text->SetXPosition(element_text->GetXPosition() + element_animation->GetBoundaryLine()->width);           break;
01228       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation && element_text) element_animation->SetXPosition(element_animation->GetXPosition() + element_text->GetBoundaryLine()->width);      break;
01229       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : break; 
01230     } 
01231 
01232   if(element_animation)
01233     {
01234       for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01235         {
01236           UI_ELEMENT* subelement = element_animation->GetComposeElements()->Get(c);
01237           if(subelement) CalculePosition(subelement, element_animation->GetBoundaryLine()->width, element_animation->GetBoundaryLine()->height);         
01238         } 
01239     }
01240 
01241   SetAroundFromSubElements(element_option);
01242 
01243   return true;
01244 }
