01357 bool UI_SKINCANVAS::CalculateBoundaryLine_EditText(UI_ELEMENT* element)
01358 {  
01359   if(!element) return false;
01360 
01361   UI_ELEMENT_EDITTEXT* element_edittext = (UI_ELEMENT_EDITTEXT*)element;
01362   GRPCANVAS*           canvas           = GetCanvas();
01363 
01364   if(!canvas) return false;
01365   
01366   canvas->Vectorfont_GetConfig()->SetSize(element_edittext->GetSizeFont());
01367 
01368   XSTRING text;
01369   if(!element_edittext->GetText()->IsEmpty()) text = element_edittext->GetText()->Get();
01370     else if(!element_edittext->GetMaskText()->IsEmpty()) text = element_edittext->GetMaskText()->Get();
01371 
01372   double fatherwidth  = 0.0f;
01373   double fatherheight = 0.0f;
01374   
01375   GetFatherSize(element, fatherwidth, fatherheight);
01376 
01377   XSTRING string_max;
01378   XDWORD  size_max   =  element_edittext->GetMaxSizeText();
01379   
01380   if(!size_max) size_max = text.GetSize();
01381   for(XDWORD c=0; c<size_max; c++)
01382     {
01383       string_max += __L("A");
01384     }
01385 
01386   switch((int)element->GetBoundaryLine()->width)
01387     {
01388       case UI_ELEMENT_TYPE_ALIGN_AUTO :             
01389       case                          0 : element->GetBoundaryLine()->width  = (double)canvas->VectorFont_GetWidth(string_max.Get());     break;
01390       case UI_ELEMENT_TYPE_ALIGN_MAX  : element->GetBoundaryLine()->width = fatherwidth;                                                break;      
01391     }
01392    
01393   switch((int)element->GetBoundaryLine()->height)
01394     {
01395       case UI_ELEMENT_TYPE_ALIGN_AUTO :             
01396       case                          0 : element->GetBoundaryLine()->height  = (double)canvas->VectorFont_GetHeight(string_max.Get());   break;
01397       case UI_ELEMENT_TYPE_ALIGN_MAX  : element->GetBoundaryLine()->height  = fatherheight;                                             break;      
01398     }
01399 
01400   return CalculePosition(element, fatherwidth, fatherheight);
01401 }
