01603       switch((int)element->GetBoundaryLine()->height)
01604         {
01605           case UI_ELEMENT_TYPE_ALIGN_AUTO   :               
01606           case                           0  : { switch(element_progressbar->GetAllocationTextType())
01607                                                   {                                           
01608                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : 
01609                                                     case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_progressrect)  maximgheight += element_progressrect->GetBoundaryLine()->height; 
01610                                                                                                           if(element_animation)     maximgheight += element_animation->GetBoundaryLine()->height;  
