01632   if(element_progressrect)  CalculePosition(element_progressrect  , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);
01633   if(element_animation)     CalculePosition(element_animation     , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);
01634   if(element_text)          CalculePosition(element_text          , element_progressbar->GetBoundaryLine()->width, element_progressbar->GetBoundaryLine()->height);  
01635 
01636 
01637   switch(element_progressbar->GetAllocationTextType())
01638     {
01639       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_NONE    : break;
01640 
01641       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_DOWN    : if(element_animation && element_text)     element_animation->SetYPosition(element_animation->GetYPosition() - element_text->GetBoundaryLine()->height);     
01642                                                             if(element_progressrect && element_text)  element_progressrect->SetYPosition(element_progressrect->GetYPosition() - (element_text->GetBoundaryLine()->height + 6));     
01643                                                             break;
01644 
01645       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_UP      : if(element_animation && element_text)     element_text->SetYPosition(element_text->GetYPosition() - element_animation->GetBoundaryLine()->height);          
01646                                                             if(element_progressrect && element_text)  element_text->SetYPosition(element_text->GetYPosition() - (element_progressrect->GetBoundaryLine()->height + 6));          
01647                                                             break;                                                           
01648 
01649       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_RIGHT   : if(element_animation && element_text)     element_text->SetXPosition(element_text->GetXPosition() + element_animation->GetBoundaryLine()->width);           
01650                                                             if(element_progressrect && element_text)  element_text->SetXPosition(element_text->GetXPosition() + element_progressrect->GetBoundaryLine()->width);    
01651                                                             break;
01652 
01653       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_LEFT    : if(element_animation && element_text)     element_animation->SetXPosition(element_animation->GetXPosition() + element_text->GetBoundaryLine()->width);      
01654                                                             if(element_progressrect && element_text)  element_progressrect->SetXPosition(element_progressrect->GetXPosition() + element_text->GetBoundaryLine()->width);    
01655                                                             break;
01656 
01657       case UI_ELEMENT_OPTION_ALLOCATION_TEXT_TYPE_CENTER  : if(element_progressrect && element_text)  
01658                                                               {
01659                                                                 element_text->SetXPosition(element_progressrect->GetXPosition() + ((element_progressrect->GetBoundaryLine()->width  - element_text->GetBoundaryLine()->width)/2));    
01660                                                                 element_text->SetYPosition(element_progressrect->GetYPosition() - ((element_progressrect->GetBoundaryLine()->height - element_text->GetBoundaryLine()->height)/2));             
01661                                                               }
01662                                                             break; 
01663     } 
01664 
01665   if(element_animation)
01666     {
01667       for(XDWORD c=0; c<element_animation->GetComposeElements()->GetSize(); c++)
01668         {
01669           UI_ELEMENT* subelement = element_animation->GetComposeElements()->Get(c);
01670           if(subelement) CalculePosition(subelement, element_animation->GetBoundaryLine()->width, element_animation->GetBoundaryLine()->height);         
01671         } 
01672     }
01673 
01674   SetAroundFromSubElements(element_progressbar);
01675 
01676   return true;
01677 }
01678 
01679 
01693 bool UI_SKINCANVAS::SetElementPosition(UI_ELEMENT* element, double x_position, double y_position)
01694 {
01695   if(!element) return false;
01696 
01697   element->GetBoundaryLine()->x = x_position;
01698   element->GetBoundaryLine()->y = y_position;
01699 
01700   bool status = CalculeBoundaryLine_AllElements(element);
01701 
01702   if(dynamic_cast<UI_ELEMENT_FORM*>(element))                                                                                                                                                                                                                                          
01703     {                                                     
01704       UI_ELEMENT_FORM* element_form = (UI_ELEMENT_FORM*)element;
01705 
01706       element_form->GetVisibleRect()->x = element_form->GetXPosition();
01707       element_form->GetVisibleRect()->y = element_form->GetYPosition();
01708     }
01709 
01710   return status;
01711 }
