01742 bool UI_SKINCANVAS::Draw_Text(UI_ELEMENT* element)  
01743 {
01744   if(!element) return false;  
01745 
01746   UI_ELEMENT_TEXT*    element_text  = (UI_ELEMENT_TEXT*)element;
01747   GRPCANVAS*          canvas        = GetCanvas();  
01748   double              x_position    = 0.0f;
01749   double              y_position    = 0.0f;  
01750   XRECT               clip_rect;                       
01751   
01752   if(!canvas) return false;
01753  
01754   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01755       
01756   if(element->MustReDraw()) 
01757     {
01758       GRP2DCOLOR_RGBA8  color(element->GetColor()->GetRed(),
01759                               element->GetColor()->GetGreen(),
01760                               element->GetColor()->GetBlue(),
01761                               element->GetColor()->GetAlpha());
01762                          
01763       
01764       canvas->Vectorfont_GetConfig()->SetColor(&color);
01765       canvas->Vectorfont_GetConfig()->SetSize(element_text->GetSizeFont());  
01766       canvas->VectorFont_Print(x_position, y_position, element_text->GetText()->Get());
01767     }
01768 
01769   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01770   
01771   return true;
