01774 
01786 bool UI_SKINCANVAS::Draw_TextBox(UI_ELEMENT* element)  
01787 { 
01788   if(!element) return false;  
01789 
01790   UI_ELEMENT_TEXTBOX* element_textbox = (UI_ELEMENT_TEXTBOX*)element;
01791   GRPCANVAS*          canvas          = GetCanvas();  
01792   double              x_position      = 0.0f;
01793   double              y_position      = 0.0f;  
01794   XRECT               clip_rect;                       
01795   
01796   if(!canvas) return false;
01797  
01798   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01799       
01800   if(element->MustReDraw()) 
01801     {
01802       GRP2DCOLOR_RGBA8                color(element->GetColor()->GetRed(),
01803                                             element->GetColor()->GetGreen(),
01804                                             element->GetColor()->GetBlue(),
01805                                             element->GetColor()->GetAlpha());
01806       XVECTOR<UI_SKIN_TEXTBOX_PART*>  parts;
01807       XVECTOR<UI_SKIN_TEXTBOX_IMAGE*> images;
01808 
01809                                
01810       canvas->Vectorfont_GetConfig()->SetColor(&color);
01811       canvas->Vectorfont_GetConfig()->SetSize(element_textbox->GetSizeFont());  
01812 
01813       TextBox_GenerateLines(element_textbox, canvas, x_position, y_position, parts, images);
01814 
01815       for(XDWORD c=0; c<parts.GetSize(); c++)
01816         {
01817           UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
01818           if(textbox_part)
01819             {
01820               GRP2DCOLOR_RGBA8 color_part(textbox_part->GetColor()->GetRed(),
01821                                           textbox_part->GetColor()->GetGreen(),
01822                                           textbox_part->GetColor()->GetBlue(),
01823                                           textbox_part->GetColor()->GetAlpha());
01824 
01825               canvas->Vectorfont_GetConfig()->SetColor(&color_part);
01826 
01827               canvas->VectorFont_Printf(textbox_part->GetXPos(), textbox_part->GetYPos(), textbox_part->GetText()->Get());   
01828 
01829 
01830               #ifdef USERINTERFACE_DEBUG 
01831               GRP2DCOLOR_RGBA8  color_debug(255, 0, 255);
01832 
01833               int width   = (int)canvas->VectorFont_GetWidth(textbox_part->GetText()->Get());
01834               int height  = (int)canvas->VectorFont_GetHeight(__L("A"));
01835 
01836               canvas->SetLineWidth(1.0f);
01837               canvas->SetLineColor(&color_debug);
01838               canvas->Rectangle(textbox_part->GetXPos() ,  
01839                                 textbox_part->GetYPos() ,
01840                                 textbox_part->GetXPos() + width , 
01841                                 textbox_part->GetYPos() - height);             
01842               #endif  
01843             }
01844         } 
01845 
01846      for(XDWORD c=0; c<images.GetSize(); c++)
01847         {
01848           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(c);
01849           if(textbox_image)
01850             {
01851               canvas->PutBitmapAlpha(textbox_image->GetBoundaryLine()->x  ,
01852                                      textbox_image->GetBoundaryLine()->y  ,
01853                                      textbox_image->GetAnimation()->GetBitmap(), 100);
01854 
01855               #ifdef USERINTERFACE_DEBUG               
01856               GRP2DCOLOR_RGBA8  color_debug(0, 0, 255);
01857 
01858               canvas->SetLineWidth(1.0f);
01859               canvas->SetLineColor(&color_debug);
01860               canvas->Rectangle(textbox_image->GetBoundaryLine()->x ,  
01861                                 textbox_image->GetBoundaryLine()->y ,
01862                                 textbox_image->GetBoundaryLine()->x + textbox_image->GetBoundaryLine()->width, 
01863                                 textbox_image->GetBoundaryLine()->y + textbox_image->GetBoundaryLine()->height);              
01864               #endif
01865             }
01866         }
01867    
01868       parts.DeleteContents();
01869       parts.DeleteAll();
01870 
01871       images.DeleteContents();
01872       images.DeleteAll();
01873     }
01874 
01875   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01876   
01877   return true;
01878 }
