01880 
01892 bool UI_SKINCANVAS::Draw_Image(UI_ELEMENT* element)  
01893 {
01894   if(!element) return false;
01895  
01896   UI_ELEMENT_IMAGE*   element_image = (UI_ELEMENT_IMAGE*)element;
01897   GRPCANVAS*          canvas        = GetCanvas(); 
01898   double              x_position    = 0.0f;
01899   double              y_position    = 0.0f;
01900   XRECT               clip_rect;                       
01901 
01902   if(!element_image->GetImage()) return false; 
01903   if(!canvas) return false;
01904 
01905   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01906 
01907   if(element->MustReDraw()) 
01908     {
01909       canvas->PutBitmapAlpha(x_position , 
01910                              y_position - element_image->GetImage()->GetHeight(), 
01911                              element_image->GetImage(), element_image->GetAlpha());
01912     }
01913 
01914   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01915     
01916   return true;
01917 }
