01919 
01931 bool UI_SKINCANVAS::Draw_Animation(UI_ELEMENT* element)  
01932 {
01933   if(!element) return false;
01934 
01935   UI_ELEMENT_ANIMATION* element_animation  = (UI_ELEMENT_ANIMATION*)element;  
01936   UI_ELEMENT_IMAGE*     element_image      = (UI_ELEMENT_IMAGE*)element_animation->GetComposeElements()->Get(element_animation->IndexImage_Get());
01937   double                x_position         = 0.0f;
01938   double                y_position         = 0.0f;  
01939   GRPCANVAS*            canvas             = GetCanvas();    
01940   XRECT                 clip_rect;          
01941   bool                  redraw             = false;
01942 
01943   if(!element_animation)  return false;
01944   if(!element_image)      return false;
01945 
01946   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
01947 
01948   if(element->MustReDraw()) 
01949     {
01950       if(element_animation)
01951         {
01952           XBYTE alpha = element_animation->GetAlpha();
01953           if(alpha < element_image->GetAlpha()) element_image->SetAlpha(alpha);
01954         }
01955 
01956       Draw(element_image);     
01957     }
01958 
01959   switch(element_animation->GetState())
01960     {
01961       case UI_ELEMENT_ANIMATION_STATE_UNKNOWN   : break;
01962 
01963       case UI_ELEMENT_ANIMATION_STATE_PLAY      : if(element_animation->GetXTimerPlay()->GetMeasureMilliSeconds() >= element_animation->GetMilliSecondsDelay())
01964                                                     {
01965                                                       element_animation->IndexImage_Add();
01966                                                       element_animation->GetXTimerPlay()->Reset();  
01967                                                       redraw = true;                                                          
01968                                                     }
01969                                                   
01970                                                   break;
01971 
01972       case UI_ELEMENT_ANIMATION_STATE_STOP      : element_animation->IndexImage_Reset();                                                  
01973                                                   redraw = true;
01974                                                   break;
01975 
01976 
01977       case UI_ELEMENT_ANIMATION_STATE_PAUSE     : break;
01978     }
01979 
01980   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
01981   
01982   if(redraw) Elements_SetToRedraw(element_animation);  
01983 
01984   return true;
01985 }
