01987 
01999 bool UI_SKINCANVAS::Draw_Option(UI_ELEMENT* element)  
02000 {
02001   if(!element) return false;
02002  
02003   UI_ELEMENT_OPTION*    element_option    = (UI_ELEMENT_OPTION*)element;
02004   UI_ELEMENT_ANIMATION* element_animation = element_option->Get_UIAnimation();
02005   UI_ELEMENT_TEXT*      element_text      = element_option->Get_UIText();
02006   double                x_position        = 0.0f;
02007   double                y_position        = 0.0f;
02008   GRPCANVAS*            canvas            = GetCanvas();
02009   XRECT                 clip_rect;              
02010 
02011   if(!canvas) return false;
02012 
02013   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_PRESELECT_MAXEDGE);
02014 
02015   if(element->MustReDraw()) 
02016     {
02017       bool isselected  = false;
02018       bool ispreselect = false;
02019 
02020       if(element_option->GetFather()) 
02021         {
02022           if(element_option->GetFather()->GetType() != UI_ELEMENT_TYPE_MULTIOPTION)
02023             {
02024               ispreselect = element_option->IsPreSelect();
02025               if(!ispreselect) ispreselect = element_option->GetFather()->IsPreSelect();    
02026             }
02027 
02028           if(element_option->GetFather()->GetType() != UI_ELEMENT_TYPE_MULTIOPTION)
02029             {
02030               isselected = element_option->IsSelected();
02031               if(!isselected) isselected = element_option->GetFather()->IsSelected();    
02032             }
02033   
02034         } 
02035        else 
02036         {
02037           isselected = element_option->IsSelected();
02038           ispreselect = element_option->IsPreSelect();
02039         }
02040 
02041       if(element->IsBlinking() && !element->GetStateBlink()) ispreselect = false;
02042 
02043       DrawBackgroundColor(element_option, canvas, x_position, y_position);
02044       
02045       if(isselected)
02046         { 
02047           GRP2DCOLOR_RGBA8  colorred(255, 0, 0);     
02048           GRP2DCOLOR_RGBA8  colorhover(0x32, 0x33, 0x36, 150);
02049   
02050           canvas->SetLineWidth(0.0f);
02051           canvas->SetLineColor(&colorred);
02052           canvas->SetFillColor(&colorhover);
02053       
02054           canvas->Rectangle(x_position  - UI_SKINCANVAS_PRESELECT_MAXEDGE                                      , 
02055                             y_position  + UI_SKINCANVAS_PRESELECT_MAXEDGE                                      ,
02056                             x_position  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_PRESELECT_MAXEDGE , 
02057                             y_position  - element->GetBoundaryLine()->height - UI_SKINCANVAS_PRESELECT_MAXEDGE , true);      
02058         }
02059        else
02060         {
02061           if(ispreselect)
02062             { 
02063               
02064               GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255);
02065               GRP2DCOLOR_RGBA8  colorhover(0x32, 0x33, 0x36, 150);
02066   
02067               canvas->SetLineWidth(0.0f);
02068               canvas->SetLineColor(&colorwhite);
02069               canvas->SetFillColor(&colorhover);
02070       
02071               canvas->Rectangle(x_position  - UI_SKINCANVAS_PRESELECT_MAXEDGE                                      , 
02072                                 y_position  + UI_SKINCANVAS_PRESELECT_MAXEDGE                                      ,
02073                                 x_position  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_PRESELECT_MAXEDGE , 
02074                                 y_position  - element->GetBoundaryLine()->height - UI_SKINCANVAS_PRESELECT_MAXEDGE , true);      
02075             }
02076          }
02077 
02078       if(element_animation)  Draw(element_animation);    
02079       if(element_text)       Draw(element_text);  
02080     }
02081 
02082   
02083   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02084 
02085   if(element_animation )
02086     {
02087       if(element_animation->GetState() == UI_ELEMENT_ANIMATION_STATE_PLAY)
02088         { 
02089           Elements_SetToRedraw(element_option);  
02090         }
02091     }
02092 
02093   return true;
02094 }
02095 
02096 
02108 bool UI_SKINCANVAS::Draw_MultiOption(UI_ELEMENT* element)
02109 {    
02110   if(!screen)  return false;
02111   if(!element) return false;
02112 
02113   UI_ELEMENT_MULTIOPTION* element_multioption  = (UI_ELEMENT_MULTIOPTION*)element;
02114   double                  x_position           = 0.0f;
02115   double                  y_position           = 0.0f;
02116   GRPCANVAS*              canvas               = GetCanvas();
02117   XRECT                   clip_rect;  
02118   
02119   if(!canvas) return false;
02120   
02121   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
02122 
02123   UI_PROPERTY_SELECTABLE_STATE actual_state = UI_PROPERTY_SELECTABLE_STATE_DEACTIVE;
02124   
02125   if(element_multioption->IsSelected()) 
02126     { 
02127       actual_state = UI_PROPERTY_SELECTABLE_STATE_SELECTED;
02128     }
02129    else 
02130     {
02131       if(element_multioption->IsPreSelect()) 
02132         {
02133           actual_state = UI_PROPERTY_SELECTABLE_STATE_PRESELECT;        
02134         }
02135        else 
02136         {
02137           if(element_multioption->IsActive()) 
02138             { 
02139               actual_state = UI_PROPERTY_SELECTABLE_STATE_ACTIVE;              
02140             }
02141         }
02142     }
02143 
02144   //if(element->MustReDraw()) 
02145     {
02146       DrawBackgroundColor(element_multioption, canvas, x_position, y_position);
02147  
02148       for(XDWORD c=0; c<element_multioption->GetComposeElements()->GetSize(); c++)
02149         {
02150           UI_ELEMENT_OPTION* element_option = (UI_ELEMENT_OPTION*)element_multioption->GetComposeElements()->Get(c);
02151           if(element_option) 
02152             {
02153               if(element_option->GetSelectableState() == actual_state) 
02154                 {
02155                   Draw(element_option);              
02156                   break;              
02157                 }       
02158             }            
02159         }
02160     }
02161       
02162   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02163 
02164   return true;
02165 }
