02221 bool UI_SKINCANVAS::Draw_EditText(UI_ELEMENT* element)  
02222 {    
02223   if(!element) return false;  
02224 
02225   UI_ELEMENT_EDITTEXT*  element_edittext  = (UI_ELEMENT_EDITTEXT*)element;
02226   double                x_position        = 0.0f;
02227   double                y_position        = 0.0f;
02228   GRPCANVAS*            canvas            = GetCanvas();    
02229   static bool           visible           = false;
02230   XRECT                 clip_rect;                       
02231   
02232   if(!canvas) return false;
02233 
02234   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_EDIT_MAXEDGE);
02235   
02236   GRP2DCOLOR_RGBA8  color(element->GetColor()->GetRed(),
02237                           element->GetColor()->GetGreen(),
02238                           element->GetColor()->GetBlue(),
02239                           element->GetColor()->GetAlpha());
02240 
02241   GRP2DCOLOR_RGBA8  bkgcolor(element->GetBackgroundColor()->GetRed(),
02242                              element->GetBackgroundColor()->GetGreen(),
02243                              element->GetBackgroundColor()->GetBlue(),
02244                              element_edittext->IsPreSelect()?element->GetBackgroundColor()->GetAlpha()/2:element->GetBackgroundColor()->GetAlpha());
02245 
02246   GRP2DCOLOR_RGBA8  linecolor;
02247 
02248   GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255, 255);
02249  
02250   if(element->MustReDraw()) 
02251     {
02252       canvas->SetLineColor(&linecolor);
02253       canvas->SetFillColor(&bkgcolor);
02254 
02255       if(element->GetRoundRect())
02256         {
02257           canvas->RoundRect(element->GetXPosition()  - UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02258                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,       
02259                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02260                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , element->GetRoundRect(), true);        
02261         }
02262        else
02263         {
02264           canvas->Rectangle(element->GetXPosition()  - UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02265                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,       
02266                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02267                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , true);
02268         }
02269    
02270                                    
02271       canvas->Vectorfont_GetConfig()->SetColor(&color);
02272       canvas->Vectorfont_GetConfig()->SetSize(element_edittext->GetSizeFont());  
02273       canvas->VectorFont_Printf(element->GetXPosition(), element->GetYPosition(), element_edittext->GetText()->Get());
02274 
02275       if(element_edittext->Cursor_IsVisible())
02276         {
02277           XDWORD    cursor_xpos = 0;
02278           XSTRING   cursor_string;
02279 
02280           element_edittext->GetText()->Copy(0, element_edittext->Cursor_GetPosition(), cursor_string);
02281 
02282           cursor_xpos = (XDWORD)GetWithString(cursor_string.Get(), element_edittext->GetSizeFont());
02283        
02284           if(element_edittext->Cursor_GetXTimerBlink()->GetMeasureMilliSeconds() >= element_edittext->Cursor_GetTimeBlink())
02285             {
02286               element_edittext->Cursor_ChangeStateBlink();   
02287               element_edittext->Cursor_GetXTimerBlink()->Reset();
02288             } 
02289  
02290           if(element_edittext->Cursor_GetStateBlink())
02291             {
02292               int cursor_size = 3;
02293 
02294               canvas->SetLineColor(&colorwhite);
02295               canvas->SetFillColor(&colorwhite);
02296 
02297               canvas->Rectangle( x_position + cursor_xpos , 
02298                                  y_position + UI_SKINCANVAS_EDIT_MAXEDGE - 4  ,
02299                                  x_position + cursor_xpos + cursor_size , 
02300                                  y_position - element_edittext->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE + 4 , true);                          
02301             }
02302         }
02303     }
02304 
02305   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02306 
02307   return true;
02308 }
02309 
02310 
02322 bool UI_SKINCANVAS::Draw_Form(UI_ELEMENT* element)
02323 {    
02324   if(!screen)  return false;
02325   if(!element) return false;
02326 
02327   UI_ELEMENT_FORM*    element_form = (UI_ELEMENT_FORM*)element;
02328   double              x_position   = 0.0f;
02329   double              y_position   = 0.0f;
02330   GRPCANVAS*          canvas       = GetCanvas();
02331   XRECT               clip_rect;  
02332   
02333   if(!canvas) return false;
02334   
02335   PreDrawFunction(element, canvas, clip_rect, x_position, y_position);
02336 
02337   if(element->MustReDraw()) 
02338     {
02339       GRP2DCOLOR_RGBA8  color(element_form->GetColor()->GetRed(),
02340                               element_form->GetColor()->GetGreen(),
02341                               element_form->GetColor()->GetBlue(),
02342                               element_form->GetColor()->GetAlpha());
02343 
02344       GRP2DCOLOR_RGBA8  linecolor(element_form->GetLineColor()->GetRed(),
02345                                   element_form->GetLineColor()->GetGreen(),
02346                                   element_form->GetLineColor()->GetBlue(),
02347                                   element_form->GetLineColor()->GetAlpha());
02348 
02349       canvas->SetLineWidth(0.5f);
02350       canvas->SetLineColor(&linecolor);
02351       canvas->SetFillColor(&color);
02352 
02353       if(element_form->GetRoundRect())
02354         {
02355           canvas->RoundRect(element_form->GetVisibleRect()->x, 
02356                             element_form->GetVisibleRect()->y,
02357                             element_form->GetVisibleRect()->x + element_form->GetVisibleRect()->width   , 
02358                             element_form->GetVisibleRect()->y - element_form->GetVisibleRect()->height  , element_form->GetRoundRect(), true);
02359         }
02360        else
02361         {
02362           canvas->Rectangle(element_form->GetVisibleRect()->x, 
02363                             element_form->GetVisibleRect()->y,
02364                             element_form->GetVisibleRect()->x + element_form->GetVisibleRect()->width   , 
02365                             element_form->GetVisibleRect()->y - element_form->GetVisibleRect()->height  ,  true);
02366         }
02367     }
02368 
02369   for(XDWORD c=0; c<element_form->GetComposeElements()->GetSize(); c++)
02370     {
02371       UI_ELEMENT* subelement = (UI_ELEMENT*)element->GetComposeElements()->Get(c);
02372       if(subelement) 
02373         {
02374           Draw(subelement);
02375         }  
02376     }
02377   
02378   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02379 
02380   return true;
02381 }
