02412 bool UI_SKINCANVAS::Draw_ListBox(UI_ELEMENT* element)
02413 {
02414   if(!element) return false;  
02415 
02416   UI_ELEMENT_LISTBOX*   element_listbox = (UI_ELEMENT_LISTBOX*)element;
02417   double                x_position      = 0.0f;
02418   double                y_position      = 0.0f;
02419   GRPCANVAS*            canvas          = GetCanvas();    
02420   static bool           visible         = false;
02421   XRECT                 clip_rect;                       
02422   
02423   if(!canvas) return false;
02424 
02425   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, UI_SKINCANVAS_EDIT_MAXEDGE);
02426 
02427   if(element->MustReDraw()) 
02428     {
02429       GRP2DCOLOR_RGBA8  color(element->GetColor()->GetRed(),
02430                               element->GetColor()->GetGreen(),
02431                               element->GetColor()->GetBlue(),
02432                               element->GetColor()->GetAlpha());
02433 
02434       GRP2DCOLOR_RGBA8  bkgcolor(element->GetBackgroundColor()->GetRed(),
02435                                  element->GetBackgroundColor()->GetGreen(),
02436                                  element->GetBackgroundColor()->GetBlue(),
02437                                  element_listbox->IsPreSelect()?element->GetBackgroundColor()->GetAlpha()/2:element->GetBackgroundColor()->GetAlpha());
02438 
02439       GRP2DCOLOR_RGBA8  linecolor;
02440 
02441       GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255, 255);
02442  
02443   
02444       canvas->SetLineColor(&linecolor);
02445       canvas->SetFillColor(&bkgcolor);
02446 
