02450                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02451                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02452                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , element->GetRoundRect(), true);
02453         }
02454        else
02455         {
02456           canvas->Rectangle(element->GetXPosition()  - UI_SKINCANVAS_EDIT_MAXEDGE                                      , 
02457                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02458                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02459                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , true);
02460         }
02461                                       
02462       canvas->Vectorfont_GetConfig()->SetColor(&color);
02463       canvas->Vectorfont_GetConfig()->SetSize(element_listbox->GetSizeFont());  
02464       canvas->VectorFont_Printf(element->GetXPosition(), element->GetYPosition(), element_listbox->GetText()->Get());
02465     }
02466   
02467   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02468 
02469   return true;
02470 }
