02472 
02484 bool UI_SKINCANVAS::Draw_ProgressBar(UI_ELEMENT* element)
02485 {
02486   if(!element) return false;  
02487 
02488   UI_ELEMENT_PROGRESSBAR* element_progressbar   = (UI_ELEMENT_PROGRESSBAR*)element;
02489   UI_ELEMENT*             element_progressrect  = element_progressbar->GetProgressRect();
02490   UI_ELEMENT_ANIMATION*   element_animation     = element_progressbar->Get_UIAnimation();
02491   UI_ELEMENT_TEXT*        element_text          = element_progressbar->Get_UIText();
02492   double                  x_position            = 0.0f;
02493   double                  y_position            = 0.0f;
02494   GRPCANVAS*              canvas                = GetCanvas();    
02495   static bool             visible               = false;
02496   XRECT                   clip_rect;                       
02497   
02498   if(!canvas) return false;
02499 
02500   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, 1);
02501 
02502   if(element->MustReDraw()) 
02503     {
02504       if(element_progressrect)
02505         {
02506           GRP2DCOLOR_RGBA8  linecolor(element_progressbar->GetLineColor()->GetRed(), element_progressbar->GetLineColor()->GetGreen(), element_progressbar->GetLineColor()->GetBlue(), element_progressbar->GetLineColor()->GetAlpha());
02507           GRP2DCOLOR_RGBA8  color    (element->GetColor()->GetRed(), element->GetColor()->GetGreen(), element->GetColor()->GetBlue(), element->GetColor()->GetAlpha());      
02508           GRP2DCOLOR_RGBA8  bkgcolor (element->GetBackgroundColor()->GetRed(), element->GetBackgroundColor()->GetGreen(), element->GetBackgroundColor()->GetBlue(), element->GetBackgroundColor()->GetAlpha());
02509           
02510           canvas->SetLineColor(&linecolor);
02511           canvas->SetFillColor(&bkgcolor);
02512 
02513           if(element->GetRoundRect())
02514             {
02515               canvas->RoundRect(element_progressrect->GetXPosition(), 
02516                                 element_progressrect->GetYPosition(),
02517                                 element_progressrect->GetXPosition() + element_progressrect->GetBoundaryLine()->width  , 
02518                                 element_progressrect->GetYPosition() - element_progressrect->GetBoundaryLine()->height , element->GetRoundRect(), true);
02519             }
02520            else
02521             { 
02522               canvas->Rectangle(element_progressrect->GetXPosition(), 
02523                                 element_progressrect->GetYPosition(),
02524                                 element_progressrect->GetXPosition() + element_progressrect->GetBoundaryLine()->width  , 
02525                                 element_progressrect->GetYPosition() - element_progressrect->GetBoundaryLine()->height , true);
02526             }
02527 
02528           double widthpercent   = element_progressrect->GetBoundaryLine()->width;
02529           double heightpercent  = element_progressrect->GetBoundaryLine()->height;  
02530                                     
02531           if(element_progressbar->ContinuousCycle_Is())
02532             {                
02533               XDWORD xpos_segment = 0;
02534               XDWORD ypos_segment = 0;
02535            
02536               switch(element_progressbar->GetDirection())
02537                 {     
02538                   case UI_ELEMENT_TYPE_DIRECTION_UNKWOWN      :
02539                     case UI_ELEMENT_TYPE_DIRECTION_HORIZONTAL : widthpercent   = (element_progressrect->GetBoundaryLine()->width  * element_progressbar->ContinuousCycle_GetSizeSegment()) / 100.0f;   
02540                                                               xpos_segment   = element_progressbar->ContinuousCycle_GetNSteps();
02541                                                               xpos_segment  += element_progressbar->ContinuousCycle_GetStepSegment();
02542                                                               element_progressbar->ContinuousCycle_SetNSteps(xpos_segment);        
02543                                                                                                                           
02544                                                               if(xpos_segment < widthpercent) 
02545                                                                 {                                                                   
02546                                                                   widthpercent = xpos_segment;                                                                  
02547                                                                   xpos_segment = 0;                                                                                                                                                                                                                                                                                                                                           
02548                                                                 }                                                                
02549                                                                else
02550                                                                 {                                                                                                                                      
02551                                                                   if((xpos_segment + widthpercent) >= element_progressrect->GetBoundaryLine()->width) 
02552                                                                     {                                                                       
02553                                                                       widthpercent = (element_progressrect->GetBoundaryLine()->width - xpos_segment);                                                                       
02554                                                                       if(!widthpercent) 
02555                                                                         {
02556                                                                           xpos_segment = 0;
02557                                                                           element_progressbar->ContinuousCycle_SetNSteps(xpos_segment);                                                                          
02558                                                                         }
02559 
02560                                                                       xpos_segment -= (XDWORD)widthpercent;   
02561                                                                     }  
02562                                                                    else
02563                                                                     {
02564                                                                       xpos_segment -= (XDWORD)widthpercent;   
02565                                                                     }                                                                                                                                                                                                                       
02566                                                                 }
02567                                                                  
02568                                                                                                                                                                                       
02569                                                               break;
02570 
02571                     case UI_ELEMENT_TYPE_DIRECTION_VERTICAL     : heightpercent  = (element_progressrect->GetBoundaryLine()->height * element_progressbar->ContinuousCycle_GetSizeSegment()) / 100.0f;   
02572                                                               ypos_segment   = element_progressbar->ContinuousCycle_GetNSteps();
02573                                                               ypos_segment  += element_progressbar->ContinuousCycle_GetStepSegment();
02574                                                               element_progressbar->ContinuousCycle_SetNSteps(ypos_segment);        
02575                                                               
02576                                                               if(ypos_segment < heightpercent) 
02577                                                                 {                                                                   
02578                                                                   heightpercent = ypos_segment;                                                                  
02579                                                                   ypos_segment = 0;                                                                                                                                                                                                                                                                                                                                           
02580                                                                 }                                                                
02581                                                                else
02582                                                                 {                                                                                                                                      
02583                                                                   if((ypos_segment + heightpercent) >= element_progressrect->GetBoundaryLine()->height) 
02584                                                                     {                                                                       
02585                                                                       heightpercent = (element_progressrect->GetBoundaryLine()->height - ypos_segment);                                                                       
02586                                                                       if(!heightpercent) 
02587                                                                         {
02588                                                                           ypos_segment = 0;
02589                                                                           element_progressbar->ContinuousCycle_SetNSteps(ypos_segment);                                                                          
02590                                                                         }
02591 
02592                                                                       ypos_segment -= (XDWORD)heightpercent;   
02593                                                                     }  
02594                                                                    else
02595                                                                     {
02596                                                                       ypos_segment -= (XDWORD)heightpercent;   
02597                                                                     }                                                                                                                                                                                                                       
02598                                                                 }
02599                                                                  
02600                                                               break;
02601                 }
02602 
02603               canvas->SetFillColor(&color);
02604 
02605               if(element->GetRoundRect())
02606                 {
02607                   canvas->RoundRect(element_progressrect->GetXPosition() + xpos_segment, 
02608                                     element_progressrect->GetYPosition() + ypos_segment,
02609                                     element_progressrect->GetXPosition() + xpos_segment + widthpercent    , 
02610                                     element_progressrect->GetYPosition() - (ypos_segment + heightpercent) , element->GetRoundRect(), true);                   
02611 
02612                 }
02613                else
02614                 {
02615                   canvas->Rectangle(element_progressrect->GetXPosition() + xpos_segment, 
02616                                     element_progressrect->GetYPosition() + ypos_segment,
02617                                     element_progressrect->GetXPosition() + xpos_segment + widthpercent    , 
02618                                     element_progressrect->GetYPosition() - (ypos_segment + heightpercent) , true);                   
02619                 }
02620             }
02621            else
02622             {                
02623               switch(element_progressbar->GetDirection())
02624                 {     
02625                   case UI_ELEMENT_TYPE_DIRECTION_UNKWOWN      :
02626                     case UI_ELEMENT_TYPE_DIRECTION_HORIZONTAL : widthpercent   = (element_progressrect->GetBoundaryLine()->width  * element_progressbar->GetLevel()) / 100.0f;   break;
02627                     case UI_ELEMENT_TYPE_DIRECTION_VERTICAL     : heightpercent  = (element_progressrect->GetBoundaryLine()->height * element_progressbar->GetLevel()) / 100.0f;   break;
02628                 }
02629                
02630               canvas->SetFillColor(&color);
02631 
02632               if(element->GetRoundRect())
02633                 {
02634                   canvas->RoundRect(element_progressrect->GetXPosition(), 
02635                                     element_progressrect->GetYPosition(),
02636                                     element_progressrect->GetXPosition() + widthpercent   , 
02637                                     element_progressrect->GetYPosition() - heightpercent  , element->GetRoundRect(), true);   
02638                 }
02639                else
02640                 {
02641                   canvas->Rectangle(element_progressrect->GetXPosition(), 
02642                                     element_progressrect->GetYPosition(),
02643                                     element_progressrect->GetXPosition() + widthpercent   , 
02644                                     element_progressrect->GetYPosition() - heightpercent  , true);          
02645                 }             
02646             } 
02647 
02648           if(element_text) Draw(element_text);  
02649             
02650         }  
02651     
02652       if(element_animation) Draw(element_animation);                    
02653     }
02654 
02655   PostDrawFunction(element, canvas, clip_rect, x_position, y_position); 
02656     
02657   if(element_progressbar->ContinuousCycle_Is()) 
02658     {
02659       if(element_progressbar->ContinuousCycle_GetXTimer()->GetMeasureMilliSeconds() > element_progressbar->ContinuousCycle_GetTimeStep())
02660         {
02661           element->SetMustReDraw(true);
02662 
02663           element_progressbar->ContinuousCycle_GetXTimer()->Reset();
02664         }
02665     }
02666   
02667   return true;
02668 }
