02753 
02754 
02771 bool UI_SKINCANVAS::PreDrawFunction(UI_ELEMENT* element, GRPCANVAS* canvas, XRECT& clip_rect, double& x_position, double& y_position, XDWORD edge)
02772 {
02773   if(!canvas) return false;
02774 
02775   x_position = element->GetXPosition();
02776   y_position = element->GetYPosition();
02777 
02778   if(element->GetFather()) GetAddPositionScrollSteps(element->GetFather(), x_position, y_position);
02779 
02780   element->SetXPositionWithScroll(x_position);
02781   element->SetYPositionWithScroll(y_position);
02782  
02783   bool createarea = false;
02784   if(element->MustReDraw()) 
02785     {
02786       createarea = true;
02787       if(GetRebuildAreaByElement(element)) createarea = false;          
02788     }
02789 
02790   if(createarea)
02791     {     
02792       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Add area: level [%d] [%s] "), element->GetZLevel(), element->GetName()->Get());
02793          
02794       CreateRebuildArea(x_position - edge, 
02795                         y_position - element->GetBoundaryLine()->height - edge, 
02796                         element->GetBoundaryLine()->width   + (edge * 2),
02797                         element->GetBoundaryLine()->height  + (edge * 2), element);                    
02798     }
02799 
02800   if(!element->GetFather())
02801     {
02802       //if(dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element))
02803         {
02804           canvas->GetClipBox(clip_rect);      
02805           canvas->SetClipBox(x_position - 1 , 
02806                              y_position     ,
02807                              x_position + element->GetBoundaryLine()->width  , 
02808                              y_position - element->GetBoundaryLine()->height - 1);       
02809         }
02810     }
02811   
02812   return true;
02813 }
02814 
02815 
02831 bool UI_SKINCANVAS::PostDrawFunction(UI_ELEMENT* element, GRPCANVAS* canvas, XRECT& clip_rect, double x_position, double y_position)
02832 {  
02833   element->SetMustReDraw(false);
02834 
02835   if(!element->GetFather())
02836     {  
02837       //if(dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element))
02838         {
02839           canvas->SetClipBox(clip_rect);
02840         }
02841     }
02842   
02843   #ifdef USERINTERFACE_DEBUG 
02844     Debug_Draw(element, x_position, y_position);            
02845   #endif
02846 
02847   return true;
02848 }
02849 
02850 
02870 double UI_SKINCANVAS::TextBox_SizeLine(UI_ELEMENT_TEXTBOX* element_textbox, GRPCANVAS* canvas, double x_position, double y_position, int nline, XSTRING& characterstr, XDWORD index_char, XVECTOR<UI_SKIN_TEXTBOX_PART*>& parts, XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>* images)
02871 {
02872   XSTRING _characterstr;
02873   XDWORD  y_pos    = 0;    
02874   double  sizeline = 0;
02875 
02876   _characterstr = characterstr;
02877                                
02878   for(XDWORD c=0; c<parts.GetSize(); c++)
02879     {
02880       UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
02881       if(textbox_part->GetLineNumber() == nline)
02882         {
02883           sizeline += canvas->VectorFont_GetWidth(textbox_part->GetText()->Get());    
02884           y_pos     = textbox_part->GetYPos();    
02885         }                                      
02886     }
02887 
02888 
02889   _characterstr.Add(element_textbox->GetText()->Get()[index_char]); 
02890   sizeline += canvas->VectorFont_GetWidth(_characterstr.Get());  
02891 
02892   if(images)
02893     {
02894       for(XDWORD c=0; c<images->GetSize(); c++)
02895         {
02896           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images->Get(c);
02897           if(textbox_image)
02898             {
02899               bool isinbox = textbox_image->GetBoundaryLine()->IsWithin((XDWORD)(x_position + sizeline), (XDWORD)(y_pos));        
02900               if(isinbox) 
02901                 {                         
02902                   sizeline += textbox_image->GetBoundaryLine()->width;
02903                 }            
02904             }                                      
02905         }
02906     }
02907 
02908   return sizeline;
02909 }
02910 
02911 
02928 bool UI_SKINCANVAS::TextBox_GenerateLines(UI_ELEMENT_TEXTBOX* element_textbox, GRPCANVAS* canvas, double x_position, double y_position, XVECTOR<UI_SKIN_TEXTBOX_PART*>& parts, XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>& images)
02929 {
02930   double                  x_text_position = x_position;
02931   double                  y_text_position = y_position - element_textbox->GetBoundaryLine()->height + (double)canvas->VectorFont_GetHeight(__L("A"));  
02932   XDWORD                  index_char      = 0;  
02933   UI_COLOR                actual_color;
02934   XDWORD                  nline           = 1;
02935   double                  lineheight      = (canvas->VectorFont_GetHeight(__L("A")) + element_textbox->GetLineSpacing());
02936   XSTRING                 text;
02937   UI_SKIN_TEXTBOX_PART*   textbox_part    = NULL;
02938   bool                    outlimit        = false;
02939   bool                    newpart         = false;  
02940 
02941   text = element_textbox->GetText()->Get();
02942   if(text.IsEmpty()) return false;
02943 
02944   actual_color.CopyFrom(element_textbox->GetColor());
02945 
02946   textbox_part = new UI_SKIN_TEXTBOX_PART();
02947   if(!textbox_part) return false;
02948    
02949   textbox_part->SetLineNumber(nline);
02950   textbox_part->SetXPos((XDWORD)x_text_position);  
02951   textbox_part->SetYPos((XDWORD)y_text_position);  
02952   textbox_part->GetColor()->CopyFrom(&actual_color);
02953   
02954   while(index_char < text.GetSize() && (!outlimit))  
02955     {
02956       XCHAR character = text.Get()[index_char];
02957 
02958       switch(character)
02959         {
02960           case __C('\n')  : x_text_position  = x_position;      
02961                             y_text_position += lineheight;                
02962                             nline++;
02963                             newpart = true;     
02964 
02965                             index_char++;                            
02966                             break;
02967 
02968           case __C('!')   : { int indexstart = index_char+1;
02969                               if(text.Get()[indexstart] == __C('['))
02970                                 {                
02971                                   int type;
02972                 
02973                                   int indexend = text.FindCharacter(__C(']'), indexstart);
02974                                   if(indexend != XSTRING_NOTFOUND)
02975                                     {                                                                      
02976                                       XCHAR*    keywords[] = {  __L("COLOR") ,
02977                                                                 __L("IMAGE") ,
02978                                                                 __L("END") ,
02979                                                              };
02980                                       XSTRING   keyword;
02981                                       bool      found_keyword = false;  
02982                                                                                     
02983                                       text.Copy(indexstart+1, indexend, keyword);    
02984 
02985                                       for(type=0; type<(sizeof(keywords)/sizeof(XCHAR*)); type++)
02986                                         {
02987                                           int keywordindex = keyword.Find(keywords[type], true);
02988                                           if(!keywordindex)
02989                                             {
02990                                               XSTRING keyword_params;
02991                                               XSTRING string;        
02992                                               
02993                                               found_keyword = true;  
02994 
02995                                               string = keywords[type];
02996                                               keywordindex += string.GetSize()+1;
02997 
02998                                               keyword.Copy(keywordindex, keyword_params);
02999                                               keyword_params.DeleteCharacter(__C(' '));
03000 
03001                                               switch(type)
03002                                                 {
03003                                                   case  0 : actual_color.SetFromString(keyword_params);
03004                                                             newpart = true;
03005                                                             break;      
03006 
03007                                                   case  1 : { UI_SKIN_TEXTBOX_IMAGE* image = new UI_SKIN_TEXTBOX_IMAGE();
03008                                                               if(image)
03009                                                                 {
03010                                                                   image->GetName()->Set(keyword_params);                                                                  
03011 
03012                                                                   UI_ANIMATION* animation = GEN_USERINTERFACE.GetOrAddAnimationCache(__L(""), image->GetName()->Get());
03013                                                                   if(animation)
03014                                                                     {
03015                                                                       image->SetAnimation(animation); 
03016                                                                       
03017                                                                       image->GetBoundaryLine()->x       = x_text_position;
03018                                                                       image->GetBoundaryLine()->y       = y_text_position - (int)(canvas->VectorFont_GetHeight(__L("A"))); 
03019                                                                       image->GetBoundaryLine()->width   = animation->GetBitmap()->GetWidth();
03020                                                                       image->GetBoundaryLine()->height  = animation->GetBitmap()->GetHeight(); // + lineheight; // + (int)(canvas->VectorFont_GetHeight(__L("A"))); 
03021                                                                       
03022                                                                       images.Add(image);                                                                     
03023                                                                     }
03024                                                                 }    
03025                                                             }
03026                                                             break;        
03027 
03028                                                   case  2 : for(int d=0; d<(sizeof(keywords)/sizeof(XCHAR*)); d++)
03029                                                               {
03030                                                                 if(!keyword_params.Compare(keywords[d], true))
03031                                                                   {
03032                                                                     switch(d)
03033                                                                       {
03034                                                                         case  0 : actual_color.CopyFrom(element_textbox->GetColor());
03035                                                                                   newpart = true;
03036                                                                                   break;
03037 
03038                                                                         case  1 : break;  
03039                                                                       }                                                                    
03040                                                                   }
03041                                                               }
03042                                                             break;                                                          
03043                                                 }
03044                                               
03045                                               break;
03046                                             }
03047                                         }
03048                                                                   
03049                                       if(found_keyword) 
03050                                         {    
03051                                           text.DeleteCharacters(index_char, (keyword.GetSize() + 3));                                         
03052                                           
03053                                                         
03054                                           if(type == 1 && (indexstart > 1))
03055                                             {
03056                                               indexstart = -2;
03057 
03058                                               if(text.Get()[indexstart] != __C(' '))  
03059                                                 {
03060                                                   text.Insert(__L(" "), indexstart);                                                                                                  
03061                                                 }
03062                                             }
03063                                             
03064                                           break;
03065                                         }
03066                                     }
03067                                 } 
03068                             }
03069 
03070                   default : { XSTRING characterstr;                              
03071                               characterstr.Add(character);
03072                               
03073                               x_text_position += (canvas->VectorFont_GetWidth(characterstr.Get())); 
03074 
03075                               UI_BOUNDARYLINE   boundaryline;
03076                               bool              isinbox    = false;
03077                               bool              isinimage  = false;  
03078                               double            imagewidth = 0.0f;
03079                                 
03080                               boundaryline.x      = element_textbox->GetXPosition();
03081                               boundaryline.y      = element_textbox->GetYPosition();
03082                               boundaryline.width  = element_textbox->GetBoundaryLine()->width;
03083                               boundaryline.height = element_textbox->GetBoundaryLine()->height;
03084 
03085                               isinbox = boundaryline.IsWithin((XDWORD)(x_text_position), (XDWORD)(y_text_position));                                                           
03086                               if(isinbox)
03087                                 {
03088                                   for(XDWORD c=0; c<images.GetSize(); c++)
03089                                     {                                      
03090                                       UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(c);
03091                                       if(textbox_image)
03092                                         {                                                                            
03093                                           isinimage = boundaryline.IsWithin((XDWORD)(x_text_position), (XDWORD)(y_text_position - textbox_image->GetBoundaryLine()->height - (int)(canvas->VectorFont_GetHeight(__L("A"))) + 1));        
03094                                           if(isinimage) 
03095                                             {
03096                                               textbox_image->GetLineNumbers()->Add(nline);
03097                                               imagewidth = textbox_image->GetBoundaryLine()->width;
03098                                               break;                                          
03099                                             }
03100                                         }
03101                                     }        
03102                                 }
03103                                       
03104                               if(isinbox)
03105                                 {      
03106                                   if(isinimage)
03107                                     {
03108                                       if(element_textbox->IsWordClipping())
03109                                         {
03110                                           if(character != __C(' '))
03111                                             {
03112                                               while(!textbox_part->GetText()->IsEmpty())
03113                                                 {  
03114                                                   if(textbox_part->GetText()->Get()[textbox_part->GetText()->GetSize()-1] == __C(' ')) break;
03115                                                   textbox_part->GetText()->DeleteLastCharacter();
03116                                                   index_char--;
03117                                                 }                                                 
03118                                             }
03119                                         }
03120 
03121                                       x_text_position += imagewidth;    
03122                                       newpart          = true;                                          
03123                                     }
03124                                    else     
03125                                     {                                        
03126                                       textbox_part->GetText()->Add(characterstr);
03127                                       index_char++;
03128                                     }
03129                                 }
03130                                else
03131                                 {  
03132                                   if(element_textbox->IsWordClipping())
03133                                     {      
03134                                       while(!textbox_part->GetText()->IsEmpty())
03135                                         {
03136                                           if(textbox_part->GetText()->Get()[textbox_part->GetText()->GetSize()-1] == __C(' ')) break;
03137                                           textbox_part->GetText()->DeleteLastCharacter();
03138                                           index_char--;
03139                                         }          
03140                                     }
03141                                     
03142                                   x_text_position  = x_position;      
03143                                   y_text_position += lineheight; 
03144                                   if(y_text_position > (y_position + lineheight)) 
03145                                     {
03146                                       outlimit = true;
03147                                       break;
03148                                     }  
03149                
03150                                   nline++;
03151 
03152                                   if(!textbox_part->GetText()->IsEmpty()) 
03153                                     {
03154                                       newpart = true;                                      
03155                                     }
03156                                    else
03157                                     {
03158                                       textbox_part->SetLineNumber(nline);
03159                                       textbox_part->SetXPos((XDWORD)x_text_position);  
03160                                       textbox_part->SetYPos((XDWORD)y_text_position);  
03161                                     }
03162                                 }                               
03163                             }
03164                             break;
03165         }
03166 
03167 
03168       if(newpart)
03169         {
03170           if(!textbox_part->GetText()->IsEmpty())    
03171             {
03172               textbox_part->SetWidth((XDWORD)canvas->VectorFont_GetWidth(textbox_part->GetText()->Get()));
03173               textbox_part->SetHeight((XDWORD)canvas->VectorFont_GetHeight(__L("A")));
03174               parts.Add(textbox_part);  
03175 
03176               textbox_part = new UI_SKIN_TEXTBOX_PART();
03177               if(!textbox_part) return false;
03178             }
03179    
03180           textbox_part->SetLineNumber(nline);
03181           textbox_part->SetXPos((XDWORD)x_text_position);  
03182           textbox_part->SetYPos((XDWORD)y_text_position);  
03183           textbox_part->GetColor()->CopyFrom(&actual_color);
03184 
03185           newpart = false;
03186         }
03187     }
03188 
03189   if(outlimit)
03190     {
03191       delete textbox_part;
03192       textbox_part = NULL;
03193     }
03194    else
03195     {
03196       parts.Add(textbox_part);  
03197     }
03198 
03199   XVECTOR<UI_SKIN_TEXTBOX_PART*>    checkparts;
03200   XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>   checkimages;
03201   XDWORD                            maxnlines   = 0;
03202 
03203   for(XDWORD c=0; c<parts.GetSize(); c++)
03204     {
03205       UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
03206       if(textbox_part) maxnlines = __MAX(maxnlines, textbox_part->GetLineNumber());  
03207     }
03208 
03209   for(XDWORD nl=0; nl<maxnlines; nl++)
03210     {     
03211       for(XDWORD p=0; p<parts.GetSize(); p++)
03212         {          
03213           UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(p);        
03214           if(textbox_part) 
03215             {
03216               if(textbox_part->GetLineNumber() == nl+1) checkparts.Add(textbox_part);
03217             }
03218         }
03219 
03220       for(XDWORD p=0; p<images.GetSize(); p++)
03221         {          
03222           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(p);        
03223           if(textbox_image) 
03224             {
03225               if(textbox_image->IsInLine(nl+1))  checkimages.Add(textbox_image);
03226             }
03227         }
03228 
03229       if(checkparts.GetSize())
03230         {  
03231           switch(element_textbox->GetTextAlignment())
03232             {
03233               /*                                      
03234                                       default   :
03235               case UI_ELEMENT_TYPE_ALIGN_LEFT     : textbox_part->SetXPos((XDWORD)x_text_position);
03236                                                   break;                                        
03237 
03238               case UI_ELEMENT_TYPE_ALIGN_RIGHT  :   { XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - canvas->VectorFont_GetWidth(characterstr.Get()));
03239                                                     textbox_part->SetXPos((XDWORD)(x_text_position) + shift);
03240                                                   }
03241                                                   break;                                                                                                  
03242 
03243               case UI_ELEMENT_TYPE_ALIGN_CENTER : { XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - canvas->VectorFont_GetWidth(characterstr.Get()))/2;
03244                                                     textbox_part->SetXPos((XDWORD)(x_text_position) + shift);
03245                                                   }
03246                                                   break;                                
03247               */  
03248 
03249 
03250                                       default   : break;
03251 
03252               case UI_ELEMENT_TYPE_ALIGN_LEFT     : break;                                      
03253 
03254               case UI_ELEMENT_TYPE_ALIGN_RIGHT  :   break;                                                                                                
03255 
03256               case UI_ELEMENT_TYPE_ALIGN_CENTER : if(checkimages.GetSize())
03257                                                     {
03258                                                       XDWORD first_size = 0;
03259 
03260                                                       UI_SKIN_TEXTBOX_IMAGE* first_image = checkimages.Get(0);                                                                  
03261                                                       if(first_image)
03262                                                         {
03263                                                           for(XDWORD p=0; p<checkparts.GetSize(); p++)
03264                                                             {          
03265                                                               UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03266                                                               if(textbox_part)
03267                                                                 {
03268                                                                   if(((textbox_part->GetXPos() + textbox_part->GetWidth()) <= first_image->GetBoundaryLine()->x) &&
03269                                                                      (textbox_part->GetXPos() >= (first_image->GetBoundaryLine()->x + first_image->GetBoundaryLine()->width)))
03270                                                                     {
03271                                                                       first_size += textbox_part->GetWidth();
03272                                                                     }
03273                                                                 }  
03274                                                             }
03275 
03276                                                           XDWORD shift = (XDWORD)(first_image->GetBoundaryLine()->x - element_textbox->GetBoundaryLine()->x - first_size);    
03277                                                           
03278                                                           for(XDWORD p=0; p<checkparts.GetSize(); p++)
03279                                                             {          
03280                                                               UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03281                                                               if(textbox_part)
03282                                                                 {
03283                                                                   if((textbox_part->GetXPos() + textbox_part->GetWidth()) <= first_image->GetBoundaryLine()->x)
03284                                                                     {
03285                                                                       textbox_part->SetXPos(textbox_part->GetXPos() + first_size);
03286                                                                     }
03287                                                                 }  
03288                                                             }
03289 
03290                                                         }
03291                                                     }
03292                                                    else
03293                                                     {
03294                                                       XDWORD sizealltext = 0;
03295                                                       for(XDWORD p=0; p<checkparts.GetSize(); p++)
03296                                                         {          
03297                                                            UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03298                                                            if(textbox_part) sizealltext += textbox_part->GetWidth();
03299                                                         }
03300                                           
03301                                                       XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - sizealltext)/2;    
03302 
03303                                                       for(XDWORD p=0; p<checkparts.GetSize(); p++)
03304                                                         {          
03305                                                            UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03306                                                            if(textbox_part) textbox_part->SetXPos(textbox_part->GetXPos()+shift); 
03307                                                         }                                                             
03308                                                     }       
03309                                                   break;                                                   
03310             }
03311             
03312   
03313         }
03314 
03315       checkparts.DeleteAll();
03316       checkimages.DeleteAll();
03317                  
03318     }
03319   
03320 
03321   return true;
03322 }
03323 
