03325 #ifdef USERINTERFACE_DEBUG 
03326 
03340 bool UI_SKINCANVAS::Debug_Draw(UI_ELEMENT* element, double x_position, double y_position)
03341 {  
03342   GRPCANVAS* canvas = GetCanvas();
03343   if(!canvas) return false;
03344   
03345   GRP2DCOLOR_RGBA8  colorred(255, 0, 0);
03346   GRP2DCOLOR_RGBA8  colorblue(0, 0, 255);
03347  
03348   canvas->SetLineWidth(1.0f);
03349   canvas->SetLineColor(&colorblue);  
03350   canvas->Circle(x_position, y_position, 5);               
03351 
03352   canvas->SetLineWidth(1.0f);
03353   canvas->SetLineColor(&colorred);
03354   canvas->Rectangle(x_position ,  
03355                     y_position ,
03356                     x_position + (element->GetBoundaryLine()->width), 
03357                     y_position - (element->GetBoundaryLine()->height));
03358   return true;
03359 }
03360 #endif
