01109   if(data.GetSize() <= 4)
01110     {
01111       XDWORD integerdec = 0;
01112 
01113       for(int c=0; c<data.GetSize(); c++)
01114         {
01115           integerdec |= data.Get()[c];
01116           if(c != data.GetSize() -1) integerdec <<= 8;
01117         }   
01118 
01119       string.Format(__L("%d"), integerdec);
01120     }
01121    else
01122     { 
01123       for(int c=0; c<data.GetSize(); c++)
01124         {
01125           string.AddFormat(__L("%02X"), data.Get()[c]);
01126         }
01127     }
01128   
01129   variant = string;
01130 
01131   return true;
01132 }
