01219 bool XBER::ConvertToObjetIdentifier(XBUFFER& data, XVARIANT& variant)
01220 {  
01221   XSTRING string;      
01222   int     length  = data.GetSize();
01223   XBYTE*  bits    = data.Get();
01224  
01225   if(length < 2 && !(length == 1 && bits[0] == 0))
01226     {
01227       return false; 
01228     }
01229 
01230   string.Format(__L("%d.%d"), (data.Get()[0]/40), (data.Get()[0]%40));
01231    
01232   size_t i = 0;
01233   while(i != length - 1)
01234     {
01235       XDWORD component = 0;
01236 
01237       while(i != length - 1)
01238         {
01239           ++i;
01240  
01241           if(component >> (32-7))
01242             {
01243               return false;      
01244             }
01245  
01246           component = (component << 7) + (bits[i] & 0x7F);
01247  
01248           if(!(bits[i] & 0x80))
01249             {
01250               break;
01251             }
01252         }
01253 
01254       string.AddFormat(__L(".%d"), component);
01255 
01256     }
01257 
01258   variant = string;
01259 
01260   return true;
01261 }
