01324 bool XBER::CalculeSize(XBUFFER& buffer, XDWORD& sizedataber, XBYTE& sizehead)
01325 {  
01326   int mode = -1;
01327   
01328   if(buffer.IsEmpty()) return false;
01329 
01330   if(!(buffer.Get()[1] & 0x80))
01331     {
01332       if(!(buffer.Get()[1]))
01333         {
01334           if(tagtype == XBER_TAGTYPE_NULL)            
01335                  mode = 0;             
01336             else mode = 3;
01337             
01338         } else mode  = 0;
01339 
01340     } else mode = 2;  
01341     
01342 
01343   switch(mode)
01344     {
01345       case  0 : // Short mode              
01346                 sizedataber = buffer.Get()[1];              
01347                 sizehead    = 2;
01348                 break;
01349 
01350       case  2 : // Long mode;
01351                 { 
01352                   int sizeofsize = (buffer.Get()[1] & 0x7F);
01353                   sizehead       = 2 + sizeofsize;
01354 
01355                   if(sizeofsize > 4)  return false;
01356                   if(!sizeofsize)     return false;                    
01357                 
01358                   memcpy((XBYTE*)&sizedataber, (XBYTE*)&buffer.Get()[2], sizeofsize);   
01359                   SWAPDWORD(sizedataber);
01360 
01361                   XBYTE bitsrotate = (32-(sizeofsize*8));
01362                   sizedataber >>= bitsrotate; 
01363                 }
01364                 break;
01365 
01366       case  3 : // Indefinite mode;                               
01367                 sizehead    = 2;
01368                 sizedataber = 0;
01369                 /*
01370                 for(XDWORD c=2; c<data.GetSize()-1; c++)
01371                   {
01372                     if(!data.Get()[c] && !data.Get()[c+1])  
01373                       {
01374                         sizedataber++;
01375                       }
01376                   }  
01377                 */
01378                 break;
01379 
01380       default : break;
01381 
01382     }
01383 
01384   return true;
01385 }
