01387 
01400 bool XBER::CodeBigNumber(XDWORD number, XBUFFER& data)
01401 {
01402   data.Delete();
01403 
01404   int value = number;
01405   int length = 0;
01406 
01407   if(value >= (268435456))
01408     { // 2 ^ 28
01409       length = 5;
01410     }
01411    else
01412     {
01413       if(value >= (2097152))
01414         { // 2 ^ 21
01415           length = 4;
01416         }
01417        else
01418         {
01419           if(value >= 16384)
01420             { // 2 ^ 14
01421               length = 3;
01422             }
01423            else
01424             {
01425               if(value >= 128)
01426                 { // 2 ^ 7
01427                   length = 2;
01428                 }
01429                else
01430                 {
01431                   length = 1;
01432                 }
01433             }
01434         }
01435     }
01436 
01437 
01438   int j = 0;
01439   for(j = length - 1; j >= 0; j--)
01440    {
01441      if(j)
01442       {
01443         int p = ((value >> (7 * j)) & 0x7F) | 0x80;
01444         data.Add((XBYTE)p);
01445       }
01446      else
01447       {
01448         int p = ((value >> (7 * j)) & 0x7F);
01449         data.Add((XBYTE)p);
01450       }
01451     }
01452 
01453   return true;
01454  }
