01457 
01470 bool XBER::CodeSize(XDWORD integer, XBUFFER& data)
01471 {
01472   data.Delete();
01473 
01474   XBYTE size = 0;
01475 
01476   if(integer&0xFF800000)
01477     {
01478       size = 4;
01479     }
01480    else
01481     {
01482       if(integer&0x00FF8000)
01483         {
01484           size = 3;
01485         }
01486        else
01487         {
01488           if(integer&0x0000FF80)
01489             {
01490               size = 2;
01491             }
01492            else
01493             {
01494               size = 1;
01495             }
01496         }
01497     }
01498 
01499   if(!size) return false;
01500 
01501   if(size>1) data.Add((XBYTE)((size-1)|0x80));
01502 
01503   for(int c=(size-data.GetSize()-1);c>=0;c--)
01504     {
01505       XBYTE byte = (XBYTE)((c?(integer>>(c*8)):(integer))&0xFF);
01506 
01507       data.Add((XBYTE)byte);
01508     }
01509 
01510   return true;
01511  }
