01528                   datestr.Format(__L("%02d%c%02d%c%04d"), day, separator, month, separator, year);
01529                 }
01530             }
01531         }
01532     }
01533 
01534   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01535     {
01536       timestr.Format(__L("%02d:%02d:%02d"), hours, minutes, seconds);
01537     }
01538    else  
01539     {
01540       timestr.Format(__L("%02d:%02d"), hours, minutes);
01541     }
01542 
01543   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01544     {
01545       timestr.AddFormat(__L(".%03d"), milliseconds);
01546     }  
01547 
01548   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME))
01549     {
01550       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01551         {
01552           string += timestr;
01553         }
01554 
01555       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))
01556         {
01557           if(!string.IsEmpty()) string += __L(" ");
01558           string += datestr;
01559         }
01560     }
01561    else
01562     {
01563       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE)) 
01564         {
01565           string += datestr;
01566         }
01567 
01568       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01569         {
01570           if(!string.IsEmpty()) string += __L(" ");
01571           string += timestr;
01572         }
01573     }
01574 
01575   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK))
01576     {
01577       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK))
01578         {
01579           XSTRING dayofweek;
01580           XSTRING string2;
01581 
01582           GetDayOfWeekString(dayofweek);
01583 
01584           string2 += dayofweek;
01585 
01586           if(!string.IsEmpty()) string2 += __L(" ");
01587 
01588           string2 += string;
01589 
01590           string = string2;
01591         }
01592        else
01593         {
01594           XSTRING dayofweek;
01595 
01596           GetDayOfWeekString(dayofweek);
01597 
01598           if(!string.IsEmpty()) string += __L(" ");
01599           string += dayofweek;
01600         }
01601     }
01602 
01603   return true;
01604 }
